SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gravar_hist_proposta ( nr_seq_proposta_p bigint, nr_seq_declaracao_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/* ie_opcao_p
	E - E-mail
*/
nr_seq_proposta_w		bigint;


BEGIN

select	max(nr_seq_proposta_adesao)
into STRICT	nr_seq_proposta_w
from	pls_declaracao_segurado
where	nr_sequencia	= nr_seq_declaracao_p;

if (coalesce(nr_seq_proposta_p,coalesce(nr_seq_proposta_w,0)) > 0) then
	if (ie_opcao_p	= 'E') then
		insert into pls_proposta_historico(	nr_sequencia,
							nr_seq_proposta,
							dt_historico,
							ds_historico,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							ie_origem_historico)
						values (	nextval('pls_proposta_historico_seq'),
							coalesce(nr_seq_proposta_p,nr_seq_proposta_w),
							clock_timestamp(),
							'Enviado e-mail pelo usuário ' || nm_usuario_p || ' referente a declaração de saúde nr. ' || nr_seq_declaracao_p,
							clock_timestamp(),
							nm_usuario_p,
							clock_timestamp(),
							nm_usuario_p,
							1);
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
