CREATE OR REPLACE PROCEDURE pls_gravar_hist_reenvio_scs ( nr_seq_guia_p bigint, nr_seq_requisicao_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	CALL pls_requisicao_gravar_hist(	nr_seq_requisicao_p, 'L', substr(
					'Transação reenviada para a operadora de origem através da função OPS - Manutenção de Processos Operacionais do Autorizador.'||chr(13)||
					'Usuário: '||nm_usuario_p||chr(13)||
					'Data: '||to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss'),1,4000),
					null, nm_usuario_p);
elsif (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	CALL pls_guia_gravar_historico(nr_seq_guia_p, 2, substr(
					'Transação reenviada para a operadora de origem através da função OPS - Manutenção de Processos Operacionais do Autorizador.'||chr(13)||
					'Usuário: '||nm_usuario_p||chr(13)||
					'Data: '||to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss'),1,4000),
					'', nm_usuario_p);
end if;

end;
$body$
