CREATE OR REPLACE PROCEDURE pls_gravar_inconsist_conta ( dt_mes_competencia_p timestamp, ie_tipo_consistencia_p text, ie_tipo_protocolo_p text, nr_seq_conta_p bigint, nr_seq_conta_proc_p bigint, nr_seq_conta_mat_p bigint, nr_seq_conta_resumo_p bigint, nr_seq_pos_estab_p bigint, nr_seq_pos_estab_contab_p bigint, nr_seq_copartic_p bigint, nr_seq_copartic_contab_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


ie_consistir_w		varchar(1);


BEGIN

select  coalesce(max('N'),'S')
into STRICT	ie_consistir_w

where   ie_tipo_consistencia_p in (5,6,7,8,9,209,311,317)
and     ie_tipo_protocolo_p <> 'C';

if (ie_consistir_w = 'S') then

	insert into pls_consist_conta(
			nr_sequencia,
			cd_estabelecimento,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			dt_mes_competencia,
			ie_tipo_consistencia,
			ds_acao,
			nr_seq_conta,
			nr_seq_conta_proc,
			nr_seq_conta_mat,
			nr_seq_conta_resumo,
			nr_seq_pos_estab,
			nr_seq_pos_estab_contab,
			nr_seq_copartic,
			nr_seq_copartic_contab)
		values (nextval('pls_consist_conta_seq'),
			cd_estabelecimento_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			dt_mes_competencia_p,
			ie_tipo_consistencia_p,
			'',
			nr_seq_conta_p,
			nr_seq_conta_proc_p,
			nr_seq_conta_mat_p,
			nr_seq_conta_resumo_p,
			nr_seq_pos_estab_p,
			nr_seq_pos_estab_contab_p,
			nr_seq_copartic_p,
			nr_seq_copartic_contab_p);

	commit;
end if;

end;
$body$
