CREATE OR REPLACE PROCEDURE pls_gravar_incons_mov_contab ( nr_seq_movimento_p bigint, cd_inconsistencia_p bigint, cd_tipo_lote_contabil_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_inconsistencia_w		bigint;
nr_seq_movto_inconsist_w	bigint;


BEGIN

select	max(a.nr_sequencia)
into STRICT	nr_seq_inconsistencia_w
from	pls_inconsistencia_contab	a,
	pls_inconsist_contab_lote	b
where	b.nr_seq_inconsistencia	= a.nr_sequencia
and	a.cd_inconsistencia	= cd_inconsistencia_p
and	b.cd_tipo_lote_contabil	= cd_tipo_lote_contabil_p
and	a.ie_situacao		= 'A';

if (coalesce(nr_seq_inconsistencia_w::text, '') = '') then
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_inconsistencia_w
	from	pls_inconsistencia_contab	a
	where	a.cd_inconsistencia	= cd_inconsistencia_p
	and	a.ie_situacao		= 'A';
end if;

if (nr_seq_inconsistencia_w IS NOT NULL AND nr_seq_inconsistencia_w::text <> '') then
	select	nextval('pls_movto_contab_inconsist_seq')
	into STRICT	nr_seq_movto_inconsist_w
	;

	insert	into	pls_movto_contab_inconsist(	nr_sequencia, nr_seq_movimento, nr_seq_inconsistencia,
			dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
			nm_usuario_nrec)
		values (	nr_seq_movto_inconsist_w, nr_seq_movimento_p, nr_seq_inconsistencia_w,
			clock_timestamp(), nm_usuario_p, clock_timestamp(),
			nm_usuario_p);
end if;

--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
