CREATE OR REPLACE PROCEDURE pls_gravar_inicio_fim_analise ( nr_seq_analise_p bigint, nr_seq_grupo_atual_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*======== IE_OPCAO_P =========*/

/* I  - Gravar início da análise	*/

/* F  - Gravar o fim da análise	*/

/*==============================*/

nr_seq_analise_grupo_w		bigint;


BEGIN
if (ie_opcao_p = 'I') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_analise_grupo_w
	from	pls_auditoria_conta_grupo
	where	nr_seq_grupo 		= nr_seq_grupo_atual_p
	and	nr_seq_analise		= nr_seq_analise_p
	and	nr_seq_ordem 		=	(SELECT	min(nr_seq_ordem)
						from	pls_auditoria_conta_grupo
						where	nr_seq_grupo 		= nr_seq_grupo_atual_p
						and	nr_seq_analise		= nr_seq_analise_p
						and	coalesce(dt_liberacao::text, '') = '');

elsif (ie_opcao_p = 'F') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_analise_grupo_w
	from	pls_auditoria_conta_grupo
	where	nr_seq_grupo 		= nr_seq_grupo_atual_p
	and	nr_seq_analise		= nr_seq_analise_p
	and	nr_seq_ordem 		=	(SELECT	max(nr_seq_ordem)
						from	pls_auditoria_conta_grupo
						where	nr_seq_grupo 		= nr_seq_grupo_atual_p
						and	nr_seq_analise		= nr_seq_analise_p
						and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> ''));

end if;

if (coalesce(nr_seq_analise_grupo_w,0) > 0) then
	if (ie_opcao_p = 'I') then
		CALL pls_alterar_status_analise_cta(nr_seq_analise_p, 'A', 'PLS_GRAVAR_INICIO_FIM_ANALISE', nm_usuario_p, null);

		insert into pls_tempo_conta_grupo(nr_sequencia,
			nr_seq_auditoria,
			dt_inicio_analise,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			dt_final_analise)
		values (nextval('pls_tempo_conta_grupo_seq'),
			nr_seq_analise_grupo_w,
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			null);
	elsif (ie_opcao_p = 'F') then
		update	pls_tempo_conta_grupo
		set	dt_final_analise 	= clock_timestamp(),
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_seq_auditoria	= nr_seq_analise_grupo_w
		and	coalesce(dt_final_analise::text, '') = '';
	end if;
end if;

/* commit; */

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gravar_inicio_fim_analise ( nr_seq_analise_p bigint, nr_seq_grupo_atual_p bigint, ie_opcao_p text, nm_usuario_p text) FROM PUBLIC;

