CREATE OR REPLACE PROCEDURE pls_gravar_log_acesso_analise ( nr_seq_analise_p bigint, nr_seq_grupo_p bigint, ie_tipo_acesso_p text, ie_acao_funcao_p text, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_log_p INOUT bigint) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
/* ie_acao_funcao_p
	E - Entrada
	S - Saída
*/
qt_log_usuario_w		bigint;
qt_log_iniciado_usuario_w	bigint;
nr_seq_log_acesso_w		bigint	:= null;


BEGIN
select	count(1)
into STRICT	qt_log_iniciado_usuario_w
from	pls_analise_log_acesso	a
where	a.nr_seq_analise	= nr_seq_analise_p
and	a.ie_tipo_acesso	= ie_tipo_acesso_p
and	((a.nr_seq_grupo_auditor	= nr_seq_grupo_p) or (coalesce(a.nr_seq_grupo_auditor::text, '') = ''))
and	a.nm_usuario_auditor	= nm_usuario_p
and	coalesce(a.dt_fim_acesso::text, '') = '';

if (qt_log_iniciado_usuario_w > 0) or (ie_acao_funcao_p = 'S') then
	update	pls_analise_log_acesso
	set	dt_fim_acesso		= clock_timestamp()
	where	nr_seq_analise		= nr_seq_analise_p
	and	ie_tipo_acesso		= ie_tipo_acesso_p
	and	((nr_seq_grupo_auditor	= nr_seq_grupo_p) or (coalesce(nr_seq_grupo_auditor::text, '') = ''))
	and	nm_usuario_auditor	= nm_usuario_p
	and	coalesce(dt_fim_acesso::text, '') = '';
end if;

if	(qt_log_iniciado_usuario_w = 0 AND ie_acao_funcao_p <> 'S') or
	(ie_acao_funcao_p = 'E' AND qt_log_iniciado_usuario_w > 0) then
	select	nextval('pls_analise_log_acesso_seq')
	into STRICT	nr_seq_log_acesso_w
	;

	insert into pls_analise_log_acesso(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_inicio_acesso,
		nr_seq_grupo_auditor,
		nm_usuario_auditor,
		ie_tipo_acesso,
		nr_seq_analise)
	values (nr_seq_log_acesso_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_grupo_p,
		nm_usuario_p,
		ie_tipo_acesso_p,
		nr_seq_analise_p);

	nr_seq_log_p	:= nr_seq_log_acesso_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gravar_log_acesso_analise ( nr_seq_analise_p bigint, nr_seq_grupo_p bigint, ie_tipo_acesso_p text, ie_acao_funcao_p text, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_log_p INOUT bigint) FROM PUBLIC;

