CREATE OR REPLACE PROCEDURE pls_gravar_log_lote_processo ( nr_seq_lote_p pls_cta_lote_processo.nr_sequencia%type) AS $body$
BEGIN

if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then
	delete	FROM pls_cta_lote_proc_conta_l
	where   nr_seq_lote_processo = nr_seq_lote_p;
	commit;

	insert into pls_cta_lote_proc_conta_l(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_lote_processo,
		nr_seq_conta)
	SELECT  nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_lote_processo,
		nr_seq_conta
	from   	pls_cta_lote_proc_conta
	where   nr_seq_lote_processo   = nr_seq_lote_p;
	commit;
end if;

end;
