CREATE OR REPLACE PROCEDURE pls_gravar_numero_cns ( nr_seq_solicitacao_p bigint, nr_cartao_nac_sus_p text, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w			pls_solicitacao_cns.cd_pessoa_fisica%type;
ie_erro_w				varchar(1) := 'N';


BEGIN

begin

update	pls_solicitacao_cns
set	nr_cartao_nac_sus	= trim(both nr_cartao_nac_sus_p),
	ie_status		= '1',
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_solicitacao_p;

select	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	pls_solicitacao_cns
where	nr_sequencia		= nr_seq_solicitacao_p;

if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then

	update	pessoa_fisica
	set	nr_cartao_nac_sus	= trim(both nr_cartao_nac_sus_p),
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	cd_pessoa_fisica 	= cd_pessoa_fisica_w;

end if;

exception
when others then
	ie_erro_w := 'S';
end;

if (ie_erro_w = 'S') then
	rollback;
else
	commit;
end if;

end;
$body$
