SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gravar_primeira_limi_benef ( nr_seq_segurado_p bigint, nr_seq_limitacao_p bigint, nr_seq_guia_p bigint, dt_utilizacao_p timestamp, nm_usuario_p text) AS $body$
DECLARE


qt_meses_intervalo_w		bigint;
ie_tipo_periodo_w		varchar(10);
qt_registros_w			bigint;
dt_inicio_w			timestamp;
dt_atual_w			timestamp;
dt_final_limitacao_w		timestamp;



BEGIN

select	qt_meses_intervalo,
	ie_tipo_periodo
into STRICT	qt_meses_intervalo_w,
	ie_tipo_periodo_w
from	pls_limitacao
where	nr_sequencia	= nr_seq_limitacao_p;

select	count(*)
into STRICT	qt_registros_w
from	pls_segurado_limitacao
where	nr_seq_segurado		= nr_seq_segurado_p
and	nr_seq_limitacao	= nr_seq_limitacao_p;

if (qt_registros_w = 0) then
	insert into pls_segurado_limitacao(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
			nr_seq_segurado,nr_seq_limitacao,DT_PRIMEIRA_UTILIZACAO,NR_SEQ_GUIA_PLANO)
	values (	nextval('pls_segurado_limitacao_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
			nr_seq_segurado_p,nr_seq_limitacao_p,dt_utilizacao_p,nr_seq_guia_p);
else
	/*Busca a maior data da limitação do beneficiário*/

	select	max(DT_PRIMEIRA_UTILIZACAO)
	into STRICT	dt_atual_w
	from	pls_segurado_limitacao
	where	nr_seq_segurado		= nr_seq_segurado_p
	and	nr_seq_limitacao	= nr_seq_limitacao_p;

	dt_inicio_w	:= dt_utilizacao_p;

	/*Mesmes*/

	if (ie_tipo_periodo_w = 'M') then

		dt_final_limitacao_w := add_months(dt_atual_w,qt_meses_intervalo_w);
	/*Semana*/

	elsif (ie_tipo_periodo_w = 'S') then
		select	dt_atual_w + (qt_meses_intervalo_w*7)
		into STRICT	dt_atual_w
		;

		select	dt_atual_w - (qt_meses_intervalo_w*7)
		into STRICT	dt_final_limitacao_w
		;
	/*Dias*/

	elsif (ie_tipo_periodo_w = 'D') then
		dt_final_limitacao_w	:= dt_atual_w + qt_meses_intervalo_w;
	end if;

	/*Caso a data de utilização sea maior que a data da regra, então grava na tabela*/

	if (dt_inicio_w > dt_final_limitacao_w-1) then
		insert into pls_segurado_limitacao(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
				nr_seq_segurado,nr_seq_limitacao,DT_PRIMEIRA_UTILIZACAO,NR_SEQ_GUIA_PLANO)
		values (	nextval('pls_segurado_limitacao_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
				nr_seq_segurado_p,nr_seq_limitacao_p,dt_inicio_w,nr_seq_guia_p);
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
