CREATE OR REPLACE PROCEDURE pls_gravar_reembolso_glosa ( cd_motivo_glosa_p text, nr_seq_reembolso_p bigint, nr_seq_reembolso_proc_p bigint, nr_seq_reemboslo_mat_p bigint, ds_observacao_p text, nm_usuario_p text, ie_evento_p text ) AS $body$
DECLARE


nr_seq_motivo_glosa_w		bigint;
qt_glosa_w					integer;
ie_consistir_w				varchar(1);
nr_seq_outorgante_w			bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_motivo_glosa_w
from	tiss_motivo_glosa
where	cd_motivo_tiss	= cd_motivo_glosa_p;

select 	max(nr_sequencia)
into STRICT	nr_seq_outorgante_w
from	pls_outorgante;

if (nr_seq_motivo_glosa_w <> 0) then
	begin

	select  coalesce(d.ie_consistir,'S')
	into STRICT	ie_consistir_w
	from	tiss_motivo_glosa a,
		pls_glosa_evento b,
		pls_outorgante_glosa d
	where	a.nr_sequencia 		= b.nr_seq_motivo_glosa
	and	d.nr_seq_evento_glosa 	= b.nr_sequencia
	and	ie_evento		= ie_evento_p
	and	nr_seq_outorgante	= nr_seq_outorgante_w
	and	a.nr_sequencia	 	= nr_seq_motivo_glosa_w;

	exception
	when others then
		ie_consistir_w := 'S';
	end;

	select	count(*)
	into STRICT	qt_glosa_w
	from	pls_reembolso_glosa
	where	nr_seq_motivo_glosa	= nr_seq_motivo_glosa_w
	and (nr_seq_reembolso	= nr_seq_reembolso_p
	or	nr_seq_reemb_proc	= nr_seq_reembolso_proc_p
	or	nr_seq_reemb_mat	= nr_seq_reemboslo_mat_p);

	if	(qt_glosa_w = 0 AND ie_consistir_w = 'S') then

		insert into pls_reembolso_glosa(nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nr_seq_reembolso,
			nr_seq_reemb_proc,
			nr_seq_reemb_mat,
			nr_seq_motivo_glosa,
			ds_observacao,
			ie_situacao)
		values (nextval('pls_reembolso_glosa_seq'),
			nm_usuario_p,
			clock_timestamp(),
			nr_seq_reembolso_p,
			nr_seq_reembolso_proc_p,
			nr_seq_reemboslo_mat_p,
			nr_seq_motivo_glosa_w,
			ds_observacao_p,
			'A');
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
