CREATE OR REPLACE PROCEDURE pls_gravar_seq_contr_mov_benef ( nr_seq_lote_mov_benef_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_contrato_w			bigint;
nr_seq_contrato_atual_w		bigint;
nr_seq_contrato_novo_w		bigint;


BEGIN

select	nr_contrato,
	nr_seq_contrato
into STRICT	nr_contrato_w,
	nr_seq_contrato_atual_w
from	PLS_LOTE_MOV_BENEF
where	nr_sequencia	= nr_seq_lote_mov_benef_p;

if (nr_contrato_w IS NOT NULL AND nr_contrato_w::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_contrato_novo_w
	from	pls_contrato
	where	nr_contrato	= nr_contrato_w;

	if (nr_seq_contrato_novo_w IS NOT NULL AND nr_seq_contrato_novo_w::text <> '') then
		update	PLS_LOTE_MOV_BENEF
		set	nr_seq_contrato	= nr_seq_contrato_novo_w
		where	nr_sequencia	= nr_seq_lote_mov_benef_p;
	end if;
else
	update	PLS_LOTE_MOV_BENEF
	set	nr_seq_contrato	 = NULL,
		nr_contrato  = NULL
	where	nr_sequencia	= nr_seq_lote_mov_benef_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
