SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_gravar_voz_atendimento ( ds_arquivo_p text, nr_seq_operador_p bigint, nr_seq_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_atendimento_w		bigint;


BEGIN

if (coalesce(nr_seq_atendimento_p,0) = 0) then
	select	max(nr_sequencia)
	into STRICT	nr_seq_atendimento_w
	from	pls_atendimento
	where	nr_seq_operador = nr_seq_operador_p;
else
	nr_seq_atendimento_w := nr_seq_atendimento_p;
end if;

CALL pls_inserir_log_atendimento('Atendimento: '||nr_seq_atendimento_w|| ' \ Atendimento parâmetro: '||nr_seq_atendimento_p||
			' \ Código operador: '||nr_seq_operador_p||' \ Arquivo: '||ds_arquivo_p,12,null,nm_usuario_p);

update	pls_atendimento
set	ds_diretorio_arquivo	= ds_arquivo_p,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia = nr_seq_atendimento_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
