CREATE OR REPLACE PROCEDURE pls_grava_hist_email_benef ( nr_seq_guia_p bigint, ds_email_p text, nm_usuario_p text, ie_tipo_hist_p text default 'B') AS $body$
BEGIN

if (ie_tipo_hist_p = 'B') then
	CALL pls_guia_gravar_historico(nr_seq_guia_p,2,'Foi enviado um e-mail para '||ds_email_p||', através da ação de menu "Enviar e-mail para o beneficiário".',
		null,nm_usuario_p);
elsif (ie_tipo_hist_p = 'P') then
	CALL pls_guia_gravar_historico(nr_seq_guia_p,2,'Foi enviado um e-mail para '||ds_email_p||', através da ação de menu "Enviar e-mail para o prestador".',
		null,nm_usuario_p);
end if;
			
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
