CREATE OR REPLACE PROCEDURE pls_guia_plano_proc_post ( ie_acao_executada_p bigint, nr_seq_autorizacao_p bigint, nr_seq_mat_p bigint, nr_seq_proc_p bigint, nr_seq_procedimento_p bigint, nr_seq_preco_pacote_p bigint, nr_seq_prestador_p text, ie_tipo_guia_p text, nr_seq_item_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
BEGIN
 
if (ie_acao_executada_p in (1,2)) then 
 
	CALL pls_lanc_auto_autorizacao(nr_seq_autorizacao_p, nr_seq_proc_p, nr_seq_mat_p, nm_usuario_p, cd_estabelecimento_p);
	 
	CALL pls_atualiza_valor_proc_aut(nr_seq_procedimento_p, 'A', nm_usuario_p);
	 
end if;
 
if (ie_acao_executada_p = 1) then 
	 
	if (coalesce(nr_seq_preco_pacote_p,0) > 0) then 
 
		CALL pls_abrir_proc_pacote(nr_seq_preco_pacote_p, 0, nr_seq_autorizacao_p, 0, 'A', nr_seq_prestador_p, ie_tipo_guia_p, clock_timestamp(), nm_usuario_p);
		 
	end if;
	 
	CALL pls_gerar_conversao_pacote(0, nr_seq_item_p, 'P', 'A', nm_usuario_p, cd_estabelecimento_p);
 
end if;
 
end;
$body$
