CREATE OR REPLACE PROCEDURE pls_importar_faixa_etaria ( nr_seq_tabela_p bigint, nr_seq_faixa_etaria_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_idade_inicial_w		smallint;
qt_idade_final_w		smallint;
nr_seq_preco_w			bigint;

c01 CURSOR FOR
	SELECT	qt_idade_inicial,
		qt_idade_final
	from	pls_faixa_etaria_item
	where	nr_seq_faixa_etaria	= nr_seq_faixa_etaria_p;


BEGIN

if (nr_seq_tabela_p IS NOT NULL AND nr_seq_tabela_p::text <> '') and (nr_seq_faixa_etaria_p IS NOT NULL AND nr_seq_faixa_etaria_p::text <> '') then
	open c01;
	loop
	fetch c01 into
		qt_idade_inicial_w,
		qt_idade_final_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		select	nextval('pls_plano_preco_seq')
		into STRICT	nr_seq_preco_w
		;

		CALL pls_consiste_faixa_etaria(nr_seq_tabela_p, nr_seq_preco_w, qt_idade_inicial_w, qt_idade_final_w,'',null,null,null,null);

		insert into pls_plano_preco(nr_sequencia, dt_atualizacao, nm_usuario,
			nr_seq_tabela, qt_idade_inicial, qt_idade_final,
			vl_preco_inicial, vl_preco_atual, tx_acrescimo,
			vl_preco_nao_subsidiado)
		values (nextval('pls_plano_preco_seq'), clock_timestamp(), nm_usuario_p,
			nr_seq_tabela_p, qt_idade_inicial_w, qt_idade_final_w,
			0, 0, 0,
			0);
		end;
	end loop;
	close c01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
