CREATE OR REPLACE PROCEDURE pls_importar_itens_inpart ( cd_cgc_fornecedor_p pls_retorno_cotacao.cd_cgc_fornecedor%type, cd_condicao_pgto_p pls_retorno_cotacao.cd_condicao_pgto%type, cd_item_p pls_retorno_cotacao.cd_item%type, cd_status_p pls_retorno_cotacao.cd_status%type, ds_observacao_p pls_retorno_cotacao.ds_observacao%type, ds_status_p pls_retorno_cotacao.ds_status%type, qt_item_p pls_retorno_cotacao.qt_item%type, vl_desconto_p pls_retorno_cotacao.vl_desconto%type, vl_unitario_p pls_retorno_cotacao.vl_unitario%type, cd_reserva_p pls_cotacao.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ds_retorno_p INOUT pls_retorno_cotacao.ds_observacao%type) AS $body$
