/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Gravar o retorno da Inpart na referente tabela (pls_retorno_cotacao).
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[]  Objetos do dicionário [X] Tasy (Delphi/Java) [] Portal []  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_cotacao_w		pls_item_cotacao.nr_seq_cotacao%type;
nr_seq_item_cotacao_w		pls_item_cotacao.nr_sequencia%type;

BEGIN

begin
	select	b.nr_seq_cotacao,
		b.nr_sequencia
	into STRICT	nr_seq_cotacao_w,
		nr_seq_item_cotacao_w
	from	pls_cotacao 		a,
		pls_item_cotacao 	b
	where	b.nr_seq_cotacao = a.nr_sequencia
	and	b.nr_seq_material = cd_item_p
	and	a.nr_sequencia = cd_reserva_p;
exception
when others then
	nr_seq_cotacao_w := null;
	nr_seq_item_cotacao_w := null;
end;
ds_retorno_p := '';
if (coalesce(cd_item_p::text, '') = '') then
	ds_retorno_p := 'Favor informar o Item';
elsif (coalesce(nr_seq_cotacao_w::text, '') = '') and (cd_item_p IS NOT NULL AND cd_item_p::text <> '') then
	ds_retorno_p := 'Item ' ||cd_item_p||' não cadastrado';
elsif (coalesce(nr_seq_cotacao_w::text, '') = '') then
	ds_retorno_p := 'Cotação '|| cd_reserva_p ||' para o Item ' ||cd_item_p||' não solicitado ';
end if;

insert into pls_retorno_cotacao(nr_sequencia, cd_cgc_fornecedor, cd_condicao_pgto,
	cd_item, cd_status, ds_observacao,
	ds_status, dt_atualizacao, dt_atualizacao_nrec,
	ie_selecionado, nm_usuario, nm_usuario_nrec,
	nr_seq_cotacao,	nr_seq_item_cotacao, qt_item,
	vl_desconto, vl_unitario)
values (nextval('pls_retorno_cotacao_seq'), cd_cgc_fornecedor_p, cd_condicao_pgto_p,
	cd_item_p, cd_status_p, ds_observacao_p,
	ds_status_p, clock_timestamp(), clock_timestamp(),
	null, nm_usuario_p, nm_usuario_p,
	nr_seq_cotacao_w, nr_seq_item_cotacao_w, qt_item_p,
	vl_desconto_p, vl_unitario_p);
commit;

end;
$body$
