CREATE OR REPLACE PROCEDURE pls_importar_mat_unimed ( cd_estabelecimento_p bigint, nm_usuario_p text, ds_versao_tiss_p text) AS $body$
DECLARE


ie_federacao_w	varchar(3);


BEGIN

begin
select	coalesce(sg_estado, 'SC')
into STRICT	ie_federacao_w
from	pls_parametros_mat_fed_sc
where	cd_estabelecimento		= cd_estabelecimento_p;
exception
when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 358471 );
end;

