SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_importa_requisicao_anexo ( ds_diretorio_p text, nm_arquivo_p text, nm_usuario_p text, ie_existe_req_p INOUT text) AS $body$
DECLARE


nr_sequencia_w			bigint;
nr_seq_auditoria_w		bigint;
qt_pos_w			bigint;
ds_assunto_w			varchar(255);
ds_email_destino_w		varchar(255);
ds_email_origem_w		varchar(255);
ds_email_w			varchar(4000);

C01 CURSOR FOR
	SELECT	ds_assunto,
		ds_email_destino,
		ds_email
	from	pls_regra_fax_analise
	where	clock_timestamp()	between(dt_inicio_vigencia) and (coalesce(dt_fim_vigencia,clock_timestamp()));


BEGIN

begin
select	(nm_arquivo_p)::numeric
into STRICT	nr_sequencia_w
;
exception
when others then
	nr_sequencia_w	:= null;
end;

if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
	begin
	select	nr_sequencia
	into STRICT	nr_seq_auditoria_w
	from	pls_auditoria
	where (nr_seq_requisicao	= nr_sequencia_w
	or	nr_seq_guia		= nr_sequencia_w);
	exception
	when others then
		nr_seq_auditoria_w	:= null;
	end;

	if (nr_seq_auditoria_w IS NOT NULL AND nr_seq_auditoria_w::text <> '') then
		ie_existe_req_p	:= 'S';
		insert	into pls_auditoria_anexo(nr_sequencia, nr_seq_auditoria, dt_anexo,
			 ds_anexo, dt_atualizacao, nm_usuario,
			 ie_tipo_login_acesso, ie_liberado_portal, ie_origem_anexo)
		values (nextval('pls_auditoria_anexo_seq'), nr_seq_auditoria_w, clock_timestamp(),
			 ds_diretorio_p, clock_timestamp(), 'JustificFax',
			 'T','S', 1);

		update	pls_auditoria
		set	ie_status	= 'A',
			dt_atualizacao	= clock_timestamp(),
			nm_usuario	= 'JustificFax'
		where	nr_sequencia	= nr_seq_auditoria_w;
	else
		ie_existe_req_p	:= 'N';
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
