SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_import_evento_ocor_fin ( ds_conteudo_p text, nr_linha_p bigint, nr_seq_lote_p pls_evento_imp_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_cpf_prest_w		varchar(14);
nr_cgc_prest_w		varchar(14);
vl_lancamento_w		double precision;
ds_observacao_w		varchar(150);


BEGIN

if (ds_conteudo_p IS NOT NULL AND ds_conteudo_p::text <> '') then

	-- tenta converter o valor para number, caso não consiga insere zero
	begin
		vl_lancamento_w := (substr(ds_conteudo_p, 15, 13) || ',' || substr(ds_conteudo_p, 28, 2))::numeric;
	exception
	when others then
		vl_lancamento_w := 0;
	end;

	-- usado trim para remover os espaços em branco
	nr_cgc_prest_w := trim(both substr(ds_conteudo_p, 1, 14));

	-- caso tenha 11 posições, então é cpf
	if (length(nr_cgc_prest_w) = 11) then

		nr_cpf_prest_w := nr_cgc_prest_w;
		nr_cgc_prest_w := null;
	end if;

	ds_observacao_w := substr(ds_conteudo_p, 30, 150);

	insert into pls_evento_imp_ocor_fin(
		nr_sequencia, dt_atualizacao, nm_usuario,
		dt_atualizacao_nrec, nm_usuario_nrec, nr_cpf_prest,
		cd_cgc_prest, vl_lancamento, ds_observacao,
		nr_linha, nr_seq_lote, ie_consistente
	) values (
		nextval('pls_evento_imp_ocor_fin_seq'), clock_timestamp(), nm_usuario_p,
		clock_timestamp(), nm_usuario_p, nr_cpf_prest_w,
		nr_cgc_prest_w, vl_lancamento_w, ds_observacao_w,
		nr_linha_p, nr_seq_lote_p, 'N' -- no momento da importação ainda não foi consistido
	);

	update	pls_evento_imp_lote
	set	dt_importacao = clock_timestamp()
	where	nr_sequencia = nr_seq_lote_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
