CREATE OR REPLACE PROCEDURE pls_import_mov_mens ( ds_conteudo_p text, ie_operacao_p text, nm_usuario_p text, nr_seq_lote_p bigint) AS $body$
BEGIN

if (ie_operacao_p = 'I') then
	insert	into	w_pls_imp_mov_mens(	dt_atualizacao, nm_usuario, ds_conteudo, nr_seq_lote)
		values (	clock_timestamp(), nm_usuario_p, ds_conteudo_p, nr_seq_lote_p);
elsif (ie_operacao_p = 'D') then
	delete from w_pls_imp_mov_mens
	where nm_usuario = nm_usuario_p;
	
	delete from w_pls_imp_mov_mens
	where dt_atualizacao < clock_timestamp() - interval '2 days';
end if;

commit;

end;
$body$
