CREATE OR REPLACE PROCEDURE pls_import_plano_contas ( ds_linha_p text, nr_seq_transacao_p bigint, ie_plano_conta_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


/* IE_PLANO_CONTA_P
	A - Ativo
	P - Passivo
	R - Receita
	D - Despesa
*/
ds_linha_w		varchar(4000);
nr_pos_inicial_w	integer;
nr_pos_final_w		integer;
nr_marcacao_w		integer;
ds_conta_w		varchar(40);
qt_registros_w		integer;
ds_plano_conta_w	varchar(255);


BEGIN

ds_linha_w		:= ds_linha_p;

select	coalesce(somente_numero(ds_linha_w),0)
into STRICT	ds_conta_w
;

select	coalesce(replace(ds_linha_w,chr(009),''),0)
into STRICT	ds_linha_w
;

ds_plano_conta_w	:= replace(ds_linha_w,0,'');
ds_plano_conta_w	:= replace(ds_plano_conta_w,1,'');
ds_plano_conta_w	:= replace(ds_plano_conta_w,2,'');
ds_plano_conta_w	:= replace(ds_plano_conta_w,3,'');
ds_plano_conta_w	:= replace(ds_plano_conta_w,4,'');
ds_plano_conta_w	:= replace(ds_plano_conta_w,5,'');
ds_plano_conta_w	:= replace(ds_plano_conta_w,6,'');
ds_plano_conta_w	:= replace(ds_plano_conta_w,7,'');
ds_plano_conta_w	:= replace(ds_plano_conta_w,8,'');
ds_plano_conta_w	:= replace(ds_plano_conta_w,9,'');

select	count(*)
into STRICT	qt_registros_w
from	diops_trans_conta
where	ds_conta		= ds_conta_w
and	nr_seq_transacao	= nr_seq_transacao_p
and	cd_estabelecimento	= cd_estabelecimento_p;

begin
if (qt_registros_w	= 0) and (ds_conta_w	> 0) then
	insert into diops_trans_conta(nr_sequencia, dt_atualizacao, nm_usuario,
		dt_atualizacao_nrec, nm_usuario_nrec, nr_seq_transacao,
		ds_conta, ie_tipo_conta, ie_tipo_atividade,
		ie_plano_conta, ds_desc_conta, cd_estabelecimento)
	values (	nextval('diops_trans_conta_seq'), clock_timestamp(), nm_usuario_p,
		clock_timestamp(), nm_usuario_p, nr_seq_transacao_p,
		ds_conta_w, 'C', '',
		ie_plano_conta_p, ds_plano_conta_w, cd_estabelecimento_p);
end if;
exception
	when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(267462,
						'DS_CONTA_W=' || DS_CONTA_W||';'||
						'QT_REGISTROS_W=' || QT_REGISTROS_W||';'||
						'DS_LINHA_W=' || DS_LINHA_W);
end;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_import_plano_contas ( ds_linha_p text, nr_seq_transacao_p bigint, ie_plano_conta_p text, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

