SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_imp_arquivo_a550 ( nr_seq_camara_contest_p ptu_camara_contestacao.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

				 
nr_seq_lote_contest_w		pls_lote_contestacao.nr_sequencia%type;
nr_seq_lote_discussao_w		pls_lote_discussao.nr_sequencia%type;
ie_tipo_arquivo_w		pls_lote_discussao.ie_tipo_arquivo%type;
qt_disc_compl_w			integer;
ie_commit_w			varchar(1) := 'N';


BEGIN 
 
if (nr_seq_camara_contest_p IS NOT NULL AND nr_seq_camara_contest_p::text <> '') then 
	CALL pls_atualiza_valores_contes( nr_seq_camara_contest_p, cd_estabelecimento_p, nm_usuario_p, 'N');
	 
	CALL pls_gerar_lote_camara_contest( nr_seq_camara_contest_p, cd_estabelecimento_p, nm_usuario_p, 'N');
	 
	CALL ptu_atualizar_status_imp_a550( nr_seq_camara_contest_p, 'IN', 'N');
	 
	-- Obtém o lote de contestação vinculado a câmara de contestação 
	select	max(nr_seq_lote_contest) 
	into STRICT	nr_seq_lote_contest_w 
	from	ptu_camara_contestacao 
	where	nr_sequencia = nr_seq_camara_contest_p;
	 
	-- Obtém o último lote de discussão gerado 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_lote_discussao_w 
	from	pls_lote_discussao 
	where	nr_seq_lote_contest = nr_seq_lote_contest_w;
	 
	-- Obtem o tipo de arquivo do último lote de discussão gerado 
	select	max(ie_tipo_arquivo) 
	into STRICT	ie_tipo_arquivo_w 
	from	pls_lote_discussao 
	where	nr_sequencia = nr_seq_lote_discussao_w;
	 
	-- E se já tem discussões do tipo 7 ou 8 (Fechamento complementar) 
	select	count(1) 
	into STRICT	qt_disc_compl_w 
	from	pls_lote_discussao 
	where	nr_seq_lote_contest = nr_seq_lote_contest_w 
	and	ie_tipo_arquivo in (7,8);
	 
	-- Se o tipo de arquivo que está sendo importado for do tipo 3 ou 4 (Fechamento parcial) 
	if (ie_tipo_arquivo_w in (3,4)) then 
		-- Exibir mensagem e para o processo 
		if (qt_disc_compl_w > 0) then 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(717186); -- Não é permitido importar arquivos do tipo fechamento parcial após importar arquivo do tipo fechamento complementar. 
		else
			ie_commit_w := 'S';
		end if;
	-- Se o tipo de arquivo que está sendo importado for do tipo 5 ou 6 (Arquivo de fechamento) 
	elsif (ie_tipo_arquivo_w in (5,6)) then 
		-- Exibir mensagem e para o processo 
		if (qt_disc_compl_w > 0) then 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(717187); -- Não é permitido importar arquivos do tipo arquivo de fechamento após importar arquivo do tipo fechamento complementar. 
		else
			ie_commit_w := 'S';
		end if;
	else 
		ie_commit_w := 'S';
	end if;
	 
	if (ie_commit_w = 'S') then 
		commit;
	end if;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
