	end;
	 
	vl_tot_fatura_w		:= pls_format_valor_imp_ptu(substr(ds_conteudo_p,62,14));
	vl_tot_contestacao_w	:= pls_format_valor_imp_ptu(substr(ds_conteudo_p,76,14));
	 
	begin 
	vl_tot_acordo_w		:= pls_format_valor_imp_ptu(substr(ds_conteudo_p,90,14));
	exception 
	when others then 
		vl_tot_acordo_w := 0;
	end;
	 
	begin 
	ie_classif_cobranca_a500_w := substr(ds_conteudo_p,141,1);
	exception 
	when others then 
		ie_classif_cobranca_a500_w := null;
	end;
	 
	begin 
	nr_nota_credito_debito_a500_w := substr(ds_conteudo_p,242,20);
	exception 
	when others then 
		nr_nota_credito_debito_a500_w := null;
	end;
	 
	begin 
	nr_nota_cred_deb_number_w := somente_numero(nr_nota_credito_debito_a500_w);
	exception 
	when others then 
		nr_nota_cred_deb_number_w := null;
	end;
	 
	begin 
	vl_total_ndc_a500_w := pls_format_valor_imp_ptu(substr(ds_conteudo_p,161,14));
	exception 
	when others then 
		vl_total_ndc_a500_w := 0;
	end;
	 
	begin 
	vl_total_contest_ndc_w := pls_format_valor_imp_ptu(substr(ds_conteudo_p,175,14));
	exception 
	when others then 
		vl_total_contest_ndc_w := 0;
	end;
	 
	begin 
	vl_total_pago_ndc_w := pls_format_valor_imp_ptu(substr(ds_conteudo_p,189,14));
	exception 
	when others then 
		vl_total_pago_ndc_w := 0;
	end;
	 
	begin 
	nr_documento2_w := substr(ds_conteudo_p,203,11);
	exception 
	when others then 
		nr_documento2_w := null;
	end;
	 
	begin 
	dt_vencimento_ndc_a500_w := to_date(substr(ds_conteudo_p,159,2)||substr(ds_conteudo_p,157,2)||substr(ds_conteudo_p,153,4),'dd/mm/yyyy');
	exception 
	when others then 
		dt_vencimento_ndc_a500_w := null;
	end;
	 
	begin 
	dt_venc_doc2_w := to_date(substr(ds_conteudo_p,220,2)||substr(ds_conteudo_p,218,2)||substr(ds_conteudo_p,214,4),'dd/mm/yyyy');
	exception 
	when others then 
		dt_venc_doc2_w := null;
	end;
	 
	ie_tp_arquivo_w		:= (substr(ds_conteudo_p,104,1))::numeric;	
	vl_total_pago_w		:= pls_format_valor_imp_ptu(substr(ds_conteudo_p,107,14));
	nr_documento_w		:= (substr(ds_conteudo_p,121,11))::numeric;	
	 
	begin 
	dt_venc_doc_w	:= to_date(substr(ds_conteudo_p,145,2)||substr(ds_conteudo_p,143,2)||substr(ds_conteudo_p,139,4),'dd/mm/yyyy');	
	exception 
	when others then 
		dt_venc_doc_w	:= null;
	end;
	 
	ie_status_conclusao_w	:= (substr(ds_conteudo_p,140,1))::numeric;
	cd_unimed_operadora_w	:= pls_obter_unimed_estab(cd_estabelecimento_p);
	tp_arq_parcial_w	:= substr(ds_conteudo_p,262,1);
	 
	if (lpad(cd_unimed_operadora_w,4,'0') = lpad(cd_unimed_credora_w,4,'0')) and (lpad(cd_unimed_operadora_w,4,'0') <> lpad(cd_unimed_origem_w,4,'0')) then 
		ie_operacao_w := 'R'; -- Faturamento 
	else
		ie_operacao_w := 'E'; -- Pagamento 
	end if;	
	 
	if (coalesce(nr_fatura_head_w,'0') <> '0') and (coalesce(nr_nota_cred_deb_number_w,0) > 0) then -- NDR A500 (Reembolso) e Fatura 
		if (ie_operacao_w = 'R') then -- Faturamento 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_contestacao_w 
			from	ptu_camara_contestacao 
			where	nr_fatura			= nr_fatura_head_w 
			and	nr_nota_credito_debito_a500	= nr_nota_credito_debito_a500_w 
			and	lpad(cd_unimed_origem,4,'0')	= lpad(cd_unimed_origem_w,4,'0') 
			and	ie_operacao 			= ie_operacao_w;
		else 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_contestacao_w 
			from	ptu_camara_contestacao 
			where	nr_fatura			= nr_fatura_head_w 
			and	nr_nota_credito_debito_a500	= nr_nota_credito_debito_a500_w 
			and	lpad(cd_unimed_destino,4,'0')	= lpad(cd_unimed_credora_w,4,'0') 
			and	ie_operacao 			= ie_operacao_w;
		end if;
		 
		if (coalesce(nr_seq_contestacao_w::text, '') = '') then 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_pls_fatura_w 
			from	pls_fatura 
			where	nr_titulo = nr_fatura_head_w 
			and	nr_titulo_ndc = nr_nota_cred_deb_number_w;
			 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_lote_contest_w 
			from	pls_lote_contestacao 
			where	nr_seq_pls_fatura = nr_seq_pls_fatura_w;
		end if;
		 
	elsif (coalesce(nr_fatura_head_w,'0') <> '0') then -- Fatura 
		if (ie_operacao_w = 'R') then -- Faturamento 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_contestacao_w 
			from	ptu_camara_contestacao 
			where	nr_fatura			= nr_fatura_head_w 
			and	lpad(cd_unimed_origem,4,'0')	= lpad(cd_unimed_origem_w,4,'0') 
			and	ie_operacao 			= ie_operacao_w;
		else 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_contestacao_w 
			from	ptu_camara_contestacao 
			where	nr_fatura			= nr_fatura_head_w 
			and	lpad(cd_unimed_destino,4,'0')	= lpad(cd_unimed_credora_w,4,'0') 
			and	ie_operacao 			= ie_operacao_w;		
		end if;
		 
		if (coalesce(nr_seq_contestacao_w::text, '') = '') then 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_pls_fatura_w 
			from	pls_fatura 
			where	nr_titulo = nr_fatura_head_w;
			 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_lote_contest_w 
			from	pls_lote_contestacao 
			where	nr_seq_pls_fatura = nr_seq_pls_fatura_w;
		end if;
	 
	elsif (coalesce(nr_nota_cred_deb_number_w,0) > 0) then -- NDR A500 (Reembolso) 
		if (ie_operacao_w = 'R') then -- Faturamento 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_contestacao_w 
			from	ptu_camara_contestacao 
			where	nr_nota_credito_debito_a500	= nr_nota_credito_debito_a500_w 
			and	lpad(cd_unimed_origem,4,'0')	= lpad(cd_unimed_origem_w,4,'0') 
			and	ie_operacao 			= ie_operacao_w;
		else 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_contestacao_w 
			from	ptu_camara_contestacao 
			where	nr_nota_credito_debito_a500	= nr_nota_credito_debito_a500_w 
			and	lpad(cd_unimed_destino,4,'0')	= lpad(cd_unimed_credora_w,4,'0') 
			and	ie_operacao 			= ie_operacao_w;		
		end if;
		 
		if (coalesce(nr_seq_contestacao_w::text, '') = '') then 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_pls_fatura_w 
			from	pls_fatura 
			where	nr_titulo_ndc = nr_nota_cred_deb_number_w;
			 
			select	max(nr_sequencia) 
			into STRICT	nr_seq_lote_contest_w 
			from	pls_lote_contestacao 
			where	nr_seq_pls_fatura = nr_seq_pls_fatura_w;
		end if;
	end if;	
	 
	if (nr_seq_contestacao_w IS NOT NULL AND nr_seq_contestacao_w::text <> '') and (ie_tp_arquivo_w in (1,5,6,7,8)) then 
		select	max(nr_seq_lote_contest) 
		into STRICT	nr_seq_lote_contest_w 
		from	ptu_camara_contestacao 
		where	nr_sequencia = nr_seq_contestacao_w;
		 
		select	max(nr_seq_ptu_fatura) 
		into STRICT	nr_seq_ptu_fatura_w 
		from	pls_lote_contestacao 
		where	nr_sequencia = nr_seq_lote_contest_w;
		 
		if (nr_seq_lote_contest_w IS NOT NULL AND nr_seq_lote_contest_w::text <> '') then 
			select	count(1) 
			into STRICT	qt_arquivo_w 
			from	pls_lote_discussao 
			where	nr_seq_lote_contest	= nr_seq_lote_contest_w 
			and	ie_tipo_arquivo		= ie_tp_arquivo_w 
			and	coalesce(ie_status,'A')	<> 'C';
		end if;
		 
		if (qt_arquivo_w > 0) or ((nr_seq_ptu_fatura_w IS NOT NULL AND nr_seq_ptu_fatura_w::text <> '') and ie_tp_arquivo_w = 1) then 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(267354,'IE_TIPO_ARQUIVO_W=' || ie_tp_arquivo_w);
		end if;
	end if;
	 
	-- Se o tipo de arquivo que está sendo importado for do tipo 3 ou 4 (Fechamento parcial) ou do tipo 5 ou 6 (Arquivo de fechamento) 
	if (ie_tp_arquivo_w in (3,4,5,6)) then 
		-- E se já tem discussões do tipo 7 ou 8 (Fechamento complementar) 
		select	count(1) 
		into STRICT	qt_arq_fec_compl_w 
		from	pls_lote_discussao 
		where	nr_seq_lote_contest = nr_seq_lote_contest_w 
		and	ie_tipo_arquivo in (7,8) -- (Fechamento complementar) 
		and	coalesce(ie_status,'A') <> 'C';
		 
		-- Se tiver discussão com o tipo de arquivo complementar exibir mensagem e para o processo 
		if (qt_arq_fec_compl_w > 0) then 
			if (ie_tp_arquivo_w in (3,4)) then 
				CALL wheb_mensagem_pck.exibir_mensagem_abort(717186); -- Não é permitido importar arquivos do tipo fechamento parcial após importar arquivo do tipo fechamento complementar. 
			else
				CALL wheb_mensagem_pck.exibir_mensagem_abort(717187); -- Não é permitido importar arquivos do tipo arquivo de fechamento após importar arquivo do tipo fechamento complementar. 
			end if;
		end if;
	end if;
	 
	if (ie_tp_arquivo_w in (7,8)) then 
		select	count(1) 
		into STRICT	qt_arquivo_w 
		from	pls_lote_discussao 
		where	nr_seq_lote_contest = nr_seq_lote_contest_w 
		and	ie_tipo_arquivo in (3,4,9) -- 3 e 4 arquivo parcial, 9 é arquivo parcial que entrou em decurso de prazo 
		and	coalesce(ie_status,'A') <> 'C';
		 
		if (qt_arquivo_w = 0) then 
			--Para importar um arquivo complementar de fechamento deve-se antes ser importado arquivo de fechamento parcial. 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(658483);
		end if;
	end if;
	 
	-- Verificar se deve atualizar o campo NR_SEQ_A500 dos serviços 
	-- Este campo é de extrema importancia para identificação dos serviços na rotina "PTU_RECONH_ITENS_CONTEST_A550" 
	if (nr_seq_pls_fatura_w IS NOT NULL AND nr_seq_pls_fatura_w::text <> '') then 
		CALL pls_atualizar_seq_a500( nr_seq_pls_fatura_w, 'N');
	end if;
	 
	select	sid, 
		serial# 
	into STRICT	nm_id_sid_w, 
		nm_id_serial_w 
	from	v$session 
	where 	audsid = userenv('SESSIONID');
	 
	if (coalesce(nr_seq_contestacao_w::text, '') = '') then	 
		select	nextval('ptu_camara_contestacao_seq') 
		into STRICT	nr_seq_contestacao_w 
		;
		 
		if (ie_novo_imp_a550_w = 'S') then 
