	cd_servico_558_w	:= (substr(ds_conteudo_p,58,8))::numeric;
	vl_serv_558_w		:= pls_format_valor_imp_ptu(substr(ds_conteudo_p,66,14));
	vl_dif_vl_inter_558_w	:= pls_format_valor_imp_ptu(substr(ds_conteudo_p,80,14));
	vl_reconh_serv_558_w	:= pls_format_valor_imp_ptu(substr(ds_conteudo_p,94,14));
	vl_acordo_serv_558_w	:= pls_format_valor_imp_ptu(substr(ds_conteudo_p,108,14));
	 
	begin 
	dt_acordo_558_w		:= to_date(substr(ds_conteudo_p,128,2)||substr(ds_conteudo_p,126,2)||substr(ds_conteudo_p,122,4),'dd/mm/yyyy');
	exception 
	when others then 
		dt_acordo_558_w := null;
	end;
	 
	tp_acordo_558_w		:= to_char(substr(ds_conteudo_p,130,2));
	qt_cobrada_558_w	:= (substr(ds_conteudo_p,132,8))::numeric /10000;
	qt_reconh_558_w		:= (substr(ds_conteudo_p,140,8))::numeric /10000;
	qt_acordada_558_w	:= (substr(ds_conteudo_p,148,8))::numeric /10000;
	nm_profissional_558_w	:= trim(both substr(ds_conteudo_p,156,40));
	sg_cons_prof_558_w	:= trim(both substr(ds_conteudo_p,196,12));
	nr_cons_prof_558_w	:= trim(both substr(ds_conteudo_p,208,15));
	sg_uf_cons_prof_558_w	:= trim(both substr(ds_conteudo_p,223,2));
	nr_autoriz_558_w	:= (substr(ds_conteudo_p,225,10))::numeric;
 
	 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_quest_rrs_w 
	from	ptu_questionamento_rrs 
	where	nr_seq_contestacao 	= nr_seq_camara_cont_p 
	and	nr_lote			= nr_lote_558_w 
	and	nr_nota			= nr_nota_558_w;
	 
	select	max(s.nr_sequencia) 
	into STRICT	nr_seq_quest_serv_rrs_w 
	from	ptu_questionamento_rrs	c, 
		ptu_quest_serv_rrs	s 
	where	c.nr_sequencia		= s.nr_seq_quest_rrs 
	and	c.nr_seq_contestacao 	= nr_seq_camara_cont_p 
	and	s.nr_seq_a500		= nr_seq_a500_558_w;
 
	if (coalesce(nr_seq_quest_serv_rrs_w::text, '') = '') then 
		insert into ptu_quest_serv_rrs(nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			nr_seq_quest_rrs, 
			nr_lote, 
			nr_nota, 
			nr_seq_a500, 
			tp_particip, 
			tp_tabela, 
			cd_servico, 
			dt_servico, 
			vl_dif_vl_inter, 
			vl_serv, 
			vl_reconh_serv, 
			vl_acordo_serv, 
			tp_acordo, 
			qt_cobrada, 
			qt_reconh, 
			qt_acordada, 
			nm_profissional, 
			dt_acordo, 
			sg_cons_prof, 
			nr_cons_prof, 
			sg_uf_cons_prof, 
			nr_autoriz, 
			nr_seq_nota_serv_rrs, 
			nr_seq_conta_mat, 
			nr_seq_conta_proc, 
			nr_nota_numerico) 
		values (nextval('ptu_quest_serv_rrs_seq'), 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_seq_quest_rrs_w, 
			nr_lote_558_w, 
			nr_nota_558_w, 
			nr_seq_a500_558_w, 
			tp_particip_558_w, 
			tp_tabela_558_w, 
			cd_servico_558_w, 
			dt_servico_558_w, 
			vl_dif_vl_inter_558_w, 
			vl_serv_558_w, 
			vl_reconh_serv_558_w, 
			vl_acordo_serv_558_w, 
			tp_acordo_558_w, 
			qt_cobrada_558_w, 
			qt_reconh_558_w, 
			qt_acordada_558_w, 
			nm_profissional_558_w, 
			dt_acordo_558_w, 
			sg_cons_prof_558_w, 
			nr_cons_prof_558_w, 
			sg_uf_cons_prof_558_w, 
			nr_autoriz_558_w, 
			null, 
			null, 
			null, 
			null) returning nr_sequencia into nr_seq_quest_serv_rrs_w;
			 
		if (ie_operacao_w = 'R') then 
			SELECT * FROM ptu_reconh_contest_reemb_a550( nr_seq_quest_serv_rrs_w, nr_seq_conta_w, nr_seq_conta_proc_w, nr_seq_conta_mat_w, nr_seq_nota_cobr_rrs_w, nr_seq_nota_serv_rrs_w) INTO STRICT nr_seq_conta_w, nr_seq_conta_proc_w, nr_seq_conta_mat_w, nr_seq_nota_cobr_rrs_w, nr_seq_nota_serv_rrs_w;
			 
			update	ptu_quest_serv_rrs 
			set	nr_seq_conta_proc	= nr_seq_conta_proc_w, 
				nr_seq_conta_mat	= nr_seq_conta_mat_w, 
				nr_seq_nota_serv_rrs	= nr_seq_nota_serv_rrs_w 
			where	nr_sequencia		= nr_seq_quest_serv_rrs_w 
			and	nr_seq_a500		= nr_seq_a500_558_w;
			 
			update	ptu_questionamento_rrs 
			set	nr_seq_conta		= nr_seq_conta_w, 
				nr_seq_nota_cobr_rrs	= nr_seq_nota_cobr_rrs_w 
			where	nr_sequencia		= nr_seq_quest_rrs_w;
		end if;
			 
	else 
		-- Caso esteja recebendo novamente 
		update	ptu_quest_serv_rrs 
		set	dt_acordo		= dt_acordo_558_w, 
			qt_acordada		= qt_acordada_558_w, 
			qt_cobrada		= qt_cobrada_558_w, 
			qt_reconh		= qt_reconh_558_w, 
			tp_acordo		= tp_acordo_558_w, 
			vl_acordo_serv		= vl_acordo_serv_558_w, 
			vl_dif_vl_inter		= vl_dif_vl_inter_558_w, 
			vl_reconh_serv		= vl_reconh_serv_558_w 
		where	nr_sequencia		= nr_seq_quest_serv_rrs_w 
		and	nr_seq_a500		= nr_seq_a500_558_w;
		 
		nr_seq_referencia_p := nr_seq_camara_cont_p;	
	end if;
	 
elsif (substr(ds_conteudo_p,9,3) = '559') then 
	if (ie_novo_imp_a550_w = 'N') then 
		select	max(nr_sequencia) 
		into STRICT	nr_seq_contestacao_w	 
		from	ptu_camara_contestacao 
		where 	nm_usuario_nrec	= nm_usuario_p 
		and	ie_operacao	= 'R' 
		and	coalesce(qt_tot_r552::text, '') = '';
	else 
		select	max(nr_sequencia) 
		into STRICT	nr_seq_contestacao_w	 
		from	ptu_camara_contestacao 
		where 	nr_sequencia	= nr_seq_camara_cont_p 
		and	ie_operacao	= 'R' 
		and	coalesce(qt_tot_r552::text, '') = '';
	end if;
	 
	qt_tot_r552_w := somente_numero(substr(ds_conteudo_p,12,5));
	 
	if (nr_seq_contestacao_w IS NOT NULL AND nr_seq_contestacao_w::text <> '') then 
		update	ptu_camara_contestacao 
		set	qt_tot_r552	= qt_tot_r552_w 
		where	nr_sequencia	= nr_seq_contestacao_w;	
	end if;
		 
	nr_seq_referencia_p := nr_seq_camara_cont_p;
	 
elsif (substr(ds_conteudo_p,9,3) = '998') then 
	ds_hash_w := trim(both substr(ds_conteudo_p,12,32));
 
	if (coalesce(nr_seq_camara_cont_p,0) > 0) or (nr_seq_camara_cont_p IS NOT NULL AND nr_seq_camara_cont_p::text <> '') then		 
		update	ptu_camara_contestacao 
		set	ds_hash		= ds_hash_w 
		where	nr_sequencia	= nr_seq_camara_cont_p;
		commit;
		 
		select	max(ld.nr_sequencia) 
		into STRICT	nr_seq_lote_disc_w 
		from	ptu_camara_contestacao	cc, 
			pls_lote_contestacao	lc, 
			pls_lote_discussao	ld 
		where	lc.nr_sequencia	= cc.nr_seq_lote_contest 
		and	lc.nr_sequencia	= ld.nr_seq_lote_contest 
		and	cc.nr_sequencia	= nr_seq_camara_cont_p 
		and	ld.ds_hash_a550	= ds_hash_w 
		and	ld.ie_status	!= 'C';
		 
		if (nr_seq_lote_disc_w IS NOT NULL AND nr_seq_lote_disc_w::text <> '') then 
			-- Não foi possível importar este arquivo, ele já foi importado gerando o lote de discussão #@NR_SEQ_LOTE_DISC#@. 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(762476,'NR_SEQ_LOTE_DISC=' || nr_seq_lote_disc_w);
		end if;
	end if;
 
	nr_seq_referencia_p := nr_seq_camara_cont_p;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_imp_camara_contest_v80 ( ds_conteudo_p text, nm_usuario_p text, nr_seq_camara_contest_p bigint, nr_seq_referencia_p INOUT bigint, cd_estabelecimento_p bigint, nr_seq_camara_cont_p INOUT bigint) FROM PUBLIC;

