SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_imp_cartoes_inter_usjrp ( nr_seq_lote_p bigint, ds_conteudo_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_conteudo_w			varchar(4000);
ds_valor_w			varchar(255);
cd_cartao_intercambio_w		varchar(30);
nm_beneficiario_w		varchar(255);
cd_usuario_plano_w		varchar(30);
nr_nova_via_solic_w		bigint;
dt_nova_via_solic_aux_w		varchar(10);
dt_nova_via_solic_w		timestamp;
dt_solicitacao_aux_w		varchar(10);
dt_solicitacao_w		timestamp;
qt_registros_w			bigint;
cd_cooperativa_w		varchar(10);
nr_seq_segurado_w		bigint;
dt_validade_cart_ant_w		timestamp;
nr_via_anterior_w		integer;


BEGIN

ds_conteudo_w	:= ds_conteudo_p;

qt_registros_w	:= 0;

while(ds_conteudo_w IS NOT NULL AND ds_conteudo_w::text <> '') loop
	if (position(';' in ds_conteudo_w) = 0) then
		ds_valor_w	:= ds_conteudo_w;
		ds_conteudo_w	:= null;
	else
		ds_valor_w	:= substr(ds_conteudo_w,1,position(';' in ds_conteudo_w) - 1);
		ds_conteudo_w	:= substr(ds_conteudo_w,position(';' in ds_conteudo_w) + 1,length(ds_conteudo_w));
	end if;

	qt_registros_w	:= qt_registros_w + 1;

	/*Cartão de intercâmbio*/

	if (qt_registros_w = 1) then
		cd_cartao_intercambio_w		:= ds_valor_w;
	/*Nome do beneficiário*/

	elsif (qt_registros_w = 2) then
		nm_beneficiario_w		:= ds_valor_w;
	/*Cartão do beneficiário*/

	elsif (qt_registros_w = 3) then
		cd_usuario_plano_w		:= ds_valor_w;
	/*Via solicitada*/

	elsif (qt_registros_w = 4) then
		nr_nova_via_solic_w		:= ds_valor_w;
	/*Data validade solicitada*/

	elsif (qt_registros_w = 5) then
		dt_nova_via_solic_aux_w		:= ds_valor_w;
		dt_nova_via_solic_w		:= to_date(dt_nova_via_solic_aux_w,'dd/mm/yyyy');
	/*Data solicitação*/

	elsif (qt_registros_w = 6) then
		dt_solicitacao_aux_w		:= ds_valor_w;
		dt_solicitacao_w		:= to_date(dt_solicitacao_aux_w,'dd/mm/yyyy');
	end if;
end loop;

select	max(b.nr_sequencia)
into STRICT	nr_seq_segurado_w
from	pls_segurado		b,
	pls_segurado_carteira	a
where	a.nr_seq_segurado	= b.nr_sequencia
and	b.ie_tipo_segurado	= 'T'
and	lpad(a.nr_cartao_intercambio,17,'0')	= cd_cartao_intercambio_w
and	coalesce(b.dt_rescisao::text, '') = '';

if (coalesce(nr_seq_segurado_w::text, '') = '') then
	cd_cooperativa_w	:= pls_obter_cd_cooperativa(cd_estabelecimento_p);

	cd_usuario_plano_w	:= lpad(cd_cooperativa_w,4,'0')||cd_usuario_plano_w;

	select	max(b.nr_sequencia)
	into STRICT	nr_seq_segurado_w
	from	pls_segurado		b,
		pls_segurado_carteira	a
	where	a.nr_seq_segurado	= b.nr_sequencia
	and	b.ie_tipo_segurado	= 'T'
	and	a.cd_usuario_plano	= cd_usuario_plano_w;
end if;

if (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then

	select	a.DT_VALIDADE_CARTEIRA,
		a.NR_VIA_SOLICITACAO,
		a.cd_usuario_plano
	into STRICT	dt_validade_cart_ant_w,
		nr_via_anterior_w,
		cd_usuario_plano_w
	from	pls_segurado		b,
		pls_segurado_carteira	a
	where	a.nr_seq_segurado	= b.nr_sequencia
	and	b.nr_sequencia	= nr_seq_segurado_w;

	insert into pls_via_cart_inter(	nr_sequencia,dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
			nr_seq_lote,nr_seq_segurado,nr_cartao_intercambio,nr_via_solicitacao,dt_validade_carteira,
			dt_emissao,dt_validade_anterior,nr_via_anterior,cd_usuario_plano)
	values (	nextval('pls_via_cart_inter_seq'),clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
			nr_seq_lote_p,nr_seq_segurado_w,cd_cartao_intercambio_w,nr_nova_via_solic_w,dt_nova_via_solic_w,
			dt_solicitacao_w,dt_validade_cart_ant_w,nr_via_anterior_w,cd_usuario_plano_w);
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_imp_cartoes_inter_usjrp ( nr_seq_lote_p bigint, ds_conteudo_p text, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

