ao_imp_p text, ie_tipo_faturamento_imp_p text, ie_obito_imp_p text, ie_indicacao_acidente_imp_p text, qt_nasc_vivos_imp_p text, qt_nasc_mortos_imp_p text, qt_nasc_vivos_prematuros_imp_p text, qt_obito_precoce_imp_p text, qt_obito_tardio_imp_p text, nr_decl_nasc_vivos_imp_p text, cd_tipo_acomodacao_imp_p text, dt_alta_imp_p timestamp, nr_seq_clinica_imp_p text, dt_atendimento_imp_p timestamp, ie_recem_nascido_imp_p text, ds_indicacao_clinica_imp_p text, cd_tipo_atend_odont_p text default null, qt_total_unidade_serv_p bigint default null, vl_total_proc_copart_p bigint default null, ds_empresa_benef_imp_p text default null, nr_telefone_benef_imp_p text default null, nm_titular_benef_imp_p text default null, nr_crm_prest_exec_imp_p text default null, uf_crm_prest_exec_imp_p text default null, dt_fim_tratamento_imp_p timestamp default null, dt_fim_tratamento_p timestamp default null, ie_motivo_encerramento_p pls_conta.ie_motivo_encerramento%type default null, ie_cobertura_especial_p pls_conta.ie_cobertura_especial%type default null, ie_regime_atendimento_p pls_conta.ie_regime_atendimento%type default null, ie_saude_ocupacional_p pls_conta.ie_saude_ocupacional%type default null) AS $body$
