ie_tipo_consulta_w		smallint;

cd_cgc_executor_imp_w		varchar(14);
cd_cpf_executor_imp_w		varchar(11);
cd_cgc_prestador_imp_w		varchar(14);
nr_seq_exec_compl_imp_w		numeric(20);
cd_exec_compl_imp_w		varchar(20);


