BEGIN
if (coalesce(nr_seq_conta_p::text, '') = '') then
	select  nextval('pls_conta_seq')
	into STRICT	nr_seq_conta_w
	;
else
	nr_seq_conta_w	:= nr_seq_conta_p;
end if;

ie_funcao_executor_imp_w := ie_funcao_executor_imp_p;

ie_valida_zero_w := pls_obter_param_padrao_funcao(3,1249);

select	max(coalesce(ie_forma_val_cart_benef, '1'))
into STRICT	ie_forma_val_cart_benef_w
from	pls_web_param_geral
where	cd_estabelecimento	=	cd_estabelecimento_p;

if (ie_forma_val_cart_benef_w = '1') then
     cd_usuario_plano_imp_ww := elimina_caracteres_especiais(cd_usuario_plano_imp_p);
	 cd_usuario_plano_imp_ww := somente_numero_char(cd_usuario_plano_imp_ww);
else
	 cd_usuario_plano_imp_ww := cd_usuario_plano_imp_p;
end if;

if (ie_valida_zero_w = 'S') then
	if (length(cd_usuario_plano_imp_ww) = 16) then
		cd_usuario_plano_imp_ww := '0' || cd_usuario_plano_imp_ww;
	end if;

	select	max(nr_seq_segurado)
	into STRICT	nr_seq_segurado_w
	from	pls_segurado_carteira
	where	cd_usuario_plano	= cd_usuario_plano_imp_ww;
	
	if (coalesce(nr_seq_segurado_w,0) > 0) then
		select 	cd_usuario_plano,
			substr(obter_cooperativa_benef(nr_seq_segurado,cd_estabelecimento_p),1,4)
		into STRICT	cd_usuario_plano_imp_w,
			cd_cooperativa_w
		from	pls_segurado_carteira
		where	nr_seq_segurado = nr_seq_segurado_w;
	else
		cd_usuario_plano_imp_w := cd_usuario_plano_imp_ww;
	end if;
else
	cd_usuario_plano_imp_w := cd_usuario_plano_imp_p;
end if;

