		coalesce(max(ie_prestador_codigo),'N')
into STRICT	ie_utiliza_codigo_w,	
		ie_idt_prest_somente_codigo_w
from	pls_param_importacao_conta
where	cd_estabelecimento = cd_estabelecimento_p;

