	into STRICT	nr_seq_exec_compl_imp_w
	from	pls_prestador
	where	upper(cd_prestador) = upper(cd_exec_compl_imp_w);

else
	nr_seq_exec_compl_imp_w	:=	nr_seq_exec_compl_imp_p;
end if;

if (coalesce(nr_seq_exec_compl_imp_w::text, '') = '') then
	nr_seq_exec_compl_imp_w		:=	nr_seq_exec_compl_imp_p;
end if;

/*Diego OS 325443 - No caso de ser enviado um valor maior que um digito (padrao do TISS no caso) op sistema nao ira gerar erro.
		Mais adiante na consistencia das contas sera gerada a glosa quanto ao problema.*/
begin
ie_tipo_consulta_w := ie_tipo_consulta_p;
exception
when others then
	ie_tipo_consulta_w := null;
end;

