if (ie_idt_prest_somente_codigo_w = 'N') then
	
	if (coalesce(cd_cgc_executor_imp_w::text, '') = '') then

		--Tratando quando CNPJ vem na tag codigoNaOperadora	
		if (length(nr_seq_prestador_exec_imp_p) = 14) then
			cd_cgc_executor_imp_w := nr_seq_prestador_exec_imp_p;
		end if;
	end if;

	if (coalesce(cd_cpf_executor_imp_p::text, '') = '') then
		--Tratando quando CPF vem na tag codigoNaOperadora	
		if (length(nr_seq_prestador_exec_imp_p) = 11) then
			cd_cpf_executor_imp_w	:= nr_seq_prestador_exec_imp_p;
		end if;
	else
		cd_cpf_executor_imp_w	:= cd_cpf_executor_imp_p;
	end if;

end if;

-- se veio o prestador solicitante no arquivo
if (nr_seq_prestador_imp_p IS NOT NULL AND nr_seq_prestador_imp_p::text <> '') then

  cd_prestador_solic_imp_w  := nr_seq_prestador_imp_p;
end if;

nr_seq_prestador_solic_imp_w := pls_obter_prestador_imp(cd_cgc_prestador_imp_w, cd_cpf_prestador_imp_p, nr_seq_prestador_imp_p, null, null, null, 'C', cd_estabelecimento_p, dt_emissao_guia_p);

