nr_crm_solic_imp_w 		:= nr_crm_solic_imp_p;
uf_crm_solic_imp_w 		:= uf_crm_solic_imp_p;
	
/*aaschlote 14/11/2014 OS 803320- Tratamento para as guias odontologicas*/

if (ie_tipo_guia_p = '11') then
	nr_crm_prest_solic_imp_w := coalesce(nr_crm_prest_solic_imp_p, nr_crm_solic_imp_w);
	uf_crm_prest_solic_imp_w := coalesce(uf_crm_prest_solic_imp_p, uf_crm_solic_imp_w);
	nr_seq_prestador_exec_imp_w	:= nr_seq_prestador_exec_imp_p;
	
	/* Se os dados do exec2 estiverem vazios, dai preencher o profissional executor com o mesmo do contrado */

	if (coalesce(nr_crm_exec_imp_p::text, '') = '') then
		sg_conselho_exec_imp_w		:= '8';
		nr_crm_exec_imp_w 		:= nr_crm_prest_exec_imp_p;
		uf_crm_exec_imp_w 		:= uf_crm_prest_exec_imp_p;
		nm_medico_executor_imp_w	:= nm_prestador_exec_imp_p;
	else
		sg_conselho_exec_imp_w		:= '8';
		nr_crm_exec_imp_w 		:= nr_crm_exec_imp_p;
		uf_crm_exec_imp_w 		:= uf_crm_exec_imp_p;
		nm_medico_executor_imp_w	:= nm_medico_executor_imp_p;
	end if;
else
	sg_conselho_exec_imp_w		:= sg_conselho_exec_imp_p;
	nr_crm_exec_imp_w 		:= nr_crm_exec_imp_p;
	uf_crm_exec_imp_w 		:= uf_crm_exec_imp_p;
	nr_seq_prestador_exec_imp_w	:= nr_seq_prestador_exec_imp_p;
	nm_medico_executor_imp_w	:= nm_medico_executor_imp_p;
end if;


