CREATE OR REPLACE PROCEDURE pls_imp_contratos_grupo_web ( nr_seq_grupo_contrato_web_p pls_grupo_contrato_web.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Importar os contratos do grupo de relacionamento para o usuario web.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta: 
[  ]  Objetos do dicionario [ x ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 

C01 CURSOR(nr_seq_grupo_contrato_pc bigint) FOR
	SELECT	a.nr_sequencia,
		a.nr_seq_contrato,         
		a.nr_seq_intercambio
	from	pls_contrato_grupo a,
		pls_contrato b
	where	a.nr_seq_contrato	= b.nr_sequencia
	and	a.nr_seq_grupo		= nr_seq_grupo_contrato_pc
	and	coalesce(b.dt_rescisao_contrato, clock_timestamp()) >= clock_timestamp()		
	
union	all

	PERFORM	a.nr_sequencia,
		a.nr_seq_contrato,         
		a.nr_seq_intercambio
	from	pls_contrato_grupo a,
		pls_intercambio b
	where	a.nr_seq_intercambio	= b.nr_sequencia
	and	a.nr_seq_grupo		= nr_seq_grupo_contrato_pc
	and	coalesce(b.dt_exclusao, clock_timestamp()) >= clock_timestamp();
	
nr_seq_grupo_contrato_w		pls_grupo_contrato.nr_sequencia%type;

BEGIN

begin
	select	nr_seq_grupo_contrato
	into STRICT	nr_seq_grupo_contrato_w
	from	pls_grupo_contrato_web
	where	nr_sequencia	= nr_seq_grupo_contrato_web_p;
exception
when others then
	nr_seq_grupo_contrato_w	:= null;
end;

if (nr_seq_grupo_contrato_w IS NOT NULL AND nr_seq_grupo_contrato_w::text <> '') then
	for C01_w in C01(nr_seq_grupo_contrato_w) loop
		begin
		insert into pls_contrato_web(	nr_sequencia, ie_situacao, nr_seq_usuario_grupo,
							dt_atualizacao, nm_usuario, dt_atualizacao_nrec,    
							nm_usuario_nrec, nr_seq_contrato, nr_seq_intercambio,     
							nr_seq_subestipulante)
					values (	nextval('pls_contrato_web_seq'), 'A', nr_seq_grupo_contrato_web_p,	
							clock_timestamp(), nm_usuario_p, clock_timestamp(),
							nm_usuario_p, C01_w.nr_seq_contrato, C01_w.nr_seq_intercambio,
							null);
		end;
	end loop;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
