CREATE OR REPLACE PROCEDURE pls_imp_dev_erro_sib ( nr_seq_lote_sib_p bigint, nr_seq_interno_p bigint, cd_usuario_plano_p text, nr_cco_p text, ie_registro_p text, nm_usuario_p text, nr_seq_dev_erro_p INOUT bigint) AS $body$
DECLARE


nr_cco_w		bigint;
ie_digito_cco_w		bigint;
ds_conteudo_erro_w	varchar(70);
ds_causa_erro_w		varchar(70);
ds_erro_w		varchar(40);
nr_seq_segurado_w	bigint;
nr_seq_dev_erro_w	bigint;


BEGIN
nr_cco_w		:= substr(nr_cco_p,1,10);
ie_digito_cco_w		:= substr(nr_cco_p,11,2);

if (ie_registro_p = '1') then
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_segurado_w
	from	pls_segurado		a,
		pls_segurado_carteira	b
	where	b.nr_seq_segurado	= a.nr_sequencia
	and	b.cd_usuario_plano	= cd_usuario_plano_p;
else
	select	max(nr_sequencia)
	into STRICT	nr_seq_segurado_w
	from	pls_segurado
	where	lpad(cd_cco,12,0) = lpad(nr_cco_p,12,0);
end if;

select	nextval('sib_devolucao_erro_seq')
into STRICT	nr_seq_dev_erro_w
;

insert into sib_devolucao_erro(	nr_sequencia,nr_seq_lote, dt_atualizacao,nm_usuario,dt_atualizacao_nrec,nm_usuario_nrec,
		ie_tipo_registro,nr_seq_interno,ie_xml,cd_usuario_plano,nr_cco,
		nr_digito_cco,ie_registro,ie_reenvio,nr_seq_segurado)
values (	nr_seq_dev_erro_w,nr_seq_lote_sib_p,clock_timestamp(),nm_usuario_p,clock_timestamp(),nm_usuario_p,
		'A',nr_seq_interno_p,'S',cd_usuario_plano_p,nr_cco_w,
		ie_digito_cco_w,ie_registro_p,'N',nr_seq_segurado_w);

nr_seq_dev_erro_p	:= nr_seq_dev_erro_w;

commit;

end;
$body$
