SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_imp_preco_fornec_unimed ( nm_usuario_p text) AS $body$
DECLARE


ds_linha_w			varchar(4000);

/* =================== CAMPOS ================== */

ds_erro_w			varchar(255);
cd_fornecedor_fed_sc_w		varchar(14);
cd_cgc_fornec_w			varchar(14);
ie_produto_w			varchar(1);
vl_soma_prod_fornec_w		bigint	:= 0;
vl_produto_w			double precision;
nr_seq_prestador_w		bigint;
nr_seq_material_w		bigint;
cd_fornecedor_fed_sc_ww		bigint;
vl_soma_valida_w		bigint;
nr_seq_mat_w			bigint;
qt_fornecedores_w		bigint;
qt_fornec_preco_w		bigint;
cd_material_w			bigint;
nr_seq_uni_fed_sc_preco_w	bigint;
cd_produto_w			bigint;
ie_possui_preco_w		bigint;
ie_possui_fornec_w		bigint;
ie_tipo_registro_w		integer;
ie_moeda_w			smallint;
dt_preco_w			timestamp;

C01 CURSOR FOR
	SELECT	ds_conteudo
	from	w_import_mat_unimed
	order by
		nr_sequencia;


BEGIN
--Validação de arquivo selecionado para a Importação
select	obter_se_somente_numero(substr(ds_conteudo,11,41))
into STRICT	ie_produto_w
from	w_import_mat_unimed
where	substr(ds_conteudo,7,1)	= 'D'  LIMIT 1;

if (ie_produto_w	= 'N') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(188179);
else
	update 	pls_mat_uni_fed_sc_preco
	set	ie_situacao	= 'I';

	open C01;
	loop
	fetch C01 into
		ds_linha_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		-- DETALHE
		if (substr(ds_linha_w,7,1) = 'D') then
			cd_fornecedor_fed_sc_w	:= substr(ds_linha_w,19,10);
			vl_produto_w		:= (substr(ds_linha_w,29,10) || ',' || substr(ds_linha_w,39,2))::numeric;

			begin
			dt_preco_w		:= to_char(to_date(substr(ds_linha_w,41,8)),'dd/mm/yyyy');
			exception
			when others then
			dt_preco_w	:= null;
			end;

			select	CASE WHEN substr(ds_linha_w,49,3)=000 THEN 1  ELSE 1 END
			into STRICT	ie_moeda_w
			;

			cd_produto_w		:= substr(ds_linha_w,11,8);

			vl_soma_prod_fornec_w	:= vl_soma_prod_fornec_w + cd_fornecedor_fed_sc_w + substr(cd_produto_w,11,7);

			-- Verifica se já possui preço cadastrado no produto atual.
			select	count(1)
			into STRICT	ie_possui_preco_w
			from	pls_mat_unimed_fed_sc b,
				pls_mat_uni_fed_sc_preco a
			where	a.nr_seq_mat_unimed	= b.nr_sequencia
			and	b.cd_material		= cd_produto_w
			and	a.dt_preco		= dt_preco_w
			and	a.cd_fornecedor_fed_sc	= cd_fornecedor_fed_sc_w; -- linha incluida - OS 385877
			select	max(coalesce(nr_sequencia,0))
			into STRICT	nr_seq_mat_w
			from	pls_mat_unimed_fed_sc
			where	cd_material		= cd_produto_w;

			-- Caso não haja preço cadastrado, cadastra um novo
			if (ie_possui_preco_w = 0) and (nr_seq_mat_w <> 0) then
				insert into pls_mat_uni_fed_sc_preco(nr_sequencia,
					nm_usuario,
					dt_atualizacao,
					nm_usuario_nrec,
					dt_atualizacao_nrec,
					cd_moeda,
					dt_preco,
					vl_preco,
					cd_fornecedor_fed_sc,
					nr_seq_mat_unimed,
					ie_situacao)
				values (nextval('pls_mat_uni_fed_sc_preco_seq'),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					ie_moeda_w,
					dt_preco_w,
					vl_produto_w,
					cd_fornecedor_fed_sc_w,
					nr_seq_mat_w,
					'A');

				-- RETIRADO TRATAMENTO - OS 530068 - UNIMED LITORAL
				--update	pls_mat_unimed_fed_sc
				--set	ie_opme		= 'S'
				--where	nr_sequencia	= nr_seq_mat_w;
				select	max(cd_fornecedor_fed_sc)
				into STRICT	cd_fornecedor_fed_sc_ww
				from	pls_mat_uni_fed_sc_preco
				where	nr_seq_mat_unimed	= nr_seq_mat_w
				and	(cd_fornecedor_fed_sc IS NOT NULL AND cd_fornecedor_fed_sc::text <> '');

				if (cd_fornecedor_fed_sc_ww IS NOT NULL AND cd_fornecedor_fed_sc_ww::text <> '') then
					select	count(1)
					into STRICT	ie_possui_fornec_w
					from	pls_fornec_mat_fed_sc
					where	cd_fornecedor	= cd_fornecedor_fed_sc_ww;
				else
					ie_possui_fornec_w	:= 1;
				end if;

				if (ie_possui_fornec_w	= 0) then
					ds_erro_w	:= sqlerrm;
					ds_erro_w	:= 'Não foi importado o arquivo de fornecedores, isto impede o processo, favor verifique.';

					CALL wheb_mensagem_pck.exibir_mensagem_abort(188180,'DS_ERRO=' || ds_erro_w);
				end if;

				select	max(cd_cgc)
				into STRICT	cd_cgc_fornec_w
				from	pls_fornec_mat_fed_sc
				where	cd_fornecedor	= cd_fornecedor_fed_sc_w;

				select	max(nr_sequencia)
				into STRICT	nr_seq_prestador_w
				from	pls_prestador a
				where	a.cd_cgc	= cd_cgc_fornec_w;

				select	max(cd_material)
				into STRICT	cd_material_w
				from	pls_mat_unimed_fed_sc a
				where	a.nr_sequencia	= nr_seq_mat_w;

				select	max(nr_sequencia)
				into STRICT	nr_seq_material_w
				from	pls_material
				where	cd_material_ops	= cd_material_w;

				if (nr_seq_prestador_w IS NOT NULL AND nr_seq_prestador_w::text <> '') and (nr_seq_material_w IS NOT NULL AND nr_seq_material_w::text <> '') then
					insert into pls_prestador_mat(nr_sequencia,
						nm_usuario,
						dt_atualizacao,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_seq_prestador,
						nr_seq_material,
						ie_liberar)
					values (nextval('pls_prestador_mat_seq'),
						nm_usuario_p,
						clock_timestamp(),
						clock_timestamp(),
						nm_usuario_p,
						nr_seq_prestador_w,
						nr_seq_material_w,
						'S');
				end if;

			--Caso haja preço cadastrado, atualiza os dados do mesmo
			elsif (ie_possui_preco_w > 0) and (nr_seq_mat_w	<> 0) then
				update	pls_mat_uni_fed_sc_preco	a
				set	a.ie_situacao		= 'A'
				where	a.nr_seq_mat_unimed	= nr_seq_mat_w
				and	a.cd_fornecedor_fed_sc	= cd_fornecedor_fed_sc_w
				and	a.dt_preco		= dt_preco_w;

				-- RETIRADO TRATAMENTO - OS 530068 - UNIMED LITORAL
				--update	pls_mat_unimed_fed_sc
				--set	ie_opme	= 'S'
				--where	nr_sequencia	= nr_seq_mat_w;
			end if;

		--TRAILER
		elsif (substr(ds_linha_w,7,1) = 'T') then
			vl_soma_valida_w	:= substr(ds_linha_w,8,16);
			-- Verifica se a somatória dos códigos dos (produtos + fornecedores) é igual a informada no trailer do arquivo. Se for diferente, exibe a mensagem.
			if (vl_soma_prod_fornec_w <> vl_soma_valida_w) then
				CALL wheb_mensagem_pck.exibir_mensagem_abort(188181);
			end if;
		end if;

		end;
	end loop;
	close C01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_imp_preco_fornec_unimed ( nm_usuario_p text) FROM PUBLIC;

