CREATE OR REPLACE PROCEDURE pls_imp_ressarcimento ( ds_oficio_p text, nr_processo_p text, nr_seq_processo_p text, cd_cgc_p text, cd_abi_p pls_processo.cd_abi%type, ds_versao_p pls_processo.ds_versao%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p text, cd_registro_ans_p pls_processo.cd_registro_ans%type, cd_cgc_operadora_p pls_processo.cd_cgc_operadora%type, ds_operadora_p pls_processo.ds_operadora%type) AS $body$
DECLARE


nr_seq_prestador_w	bigint;
cd_estabelecimento_w	smallint;
cd_cgc_w		varchar(14);


BEGIN

begin
	select	max(cd_cgc)
	into STRICT	cd_cgc_w
	from 	pessoa_juridica
	where	cd_cgc	= cd_cgc_p;
exception
when others then
	cd_cgc_w := null;
end;


if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '')	then
	insert into  pls_processo(   nr_sequencia, cd_estabelecimento, dt_processo,
				     ds_oficio, nr_processo, ie_tipo_processo,
				     dt_atualizacao, nm_usuario, cd_cgc_requerente, ie_status, cd_abi,
				     ds_versao, ie_persus, cd_registro_ans, cd_cgc_operadora,
				     ds_operadora)
			values (  nr_seq_processo_p, cd_estabelecimento_p, clock_timestamp(),
				     ds_oficio_p, nr_processo_p,'SUS',
				     clock_timestamp(), substr(nm_usuario_p,1,15), cd_cgc_w, 'R', cd_abi_p,
				     ds_versao_p,'P', cd_registro_ans_p, cd_cgc_operadora_p,
				     ds_operadora_p);
end if;

commit;

end;
