CREATE OR REPLACE PROCEDURE pls_inativar_alteracao_produto ( nr_seq_segurado_p pls_segurado.nr_sequencia%type, nr_seq_alteracao_prod_p pls_segurado_alt_plano.nr_sequencia%type, dt_data_alteracao_p pls_segurado_alt_plano.dt_alteracao%type, ie_commit_p text, nm_usuario_p text) AS $body$
BEGIN

update	pls_segurado_alt_plano
set	ie_situacao	= 'I',
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp(),
	ds_observacao	= ds_observacao || ' - Registro inativado'
where	nr_seq_segurado = nr_seq_segurado_p
and	dt_alteracao    >= trunc(dt_data_alteracao_p, 'dd')
and	nr_sequencia	<> nr_seq_alteracao_prod_p;

if (coalesce(ie_commit_p, 'N') = 'S') then
	commit;
end if;

end;
$body$
