CREATE OR REPLACE PROCEDURE pls_inativar_glosa_oco_conta ( nr_seq_conta_p bigint, nm_usuario_p text) AS $body$
DECLARE



qt_item_proc_w		bigint;
qt_item_mat_w		bigint;
qt_item_lib_mat_w	bigint;
qt_item_lib_proc_w	bigint;
qt_item_w		bigint;
qt_item_lib_w		bigint;


BEGIN

update	pls_conta_glosa
set	ie_situacao 		= 'I',
	ie_forma_inativacao	= CASE WHEN ie_forma_inativacao='S' THEN 'US' WHEN ie_forma_inativacao='US' THEN 'US'  ELSE 'U' END
where	nr_seq_conta 		= nr_seq_conta_p
and	coalesce(nr_seq_conta_proc::text, '') = ''
and	coalesce(nr_seq_conta_mat::text, '') = '';

update	pls_ocorrencia_benef
set	ie_situacao 		= 'I',
	ie_forma_inativacao	= CASE WHEN ie_forma_inativacao='S' THEN 'US' WHEN ie_forma_inativacao='US' THEN 'US'  ELSE 'U' END
where	nr_seq_conta 		= nr_seq_conta_p
and	coalesce(nr_seq_conta_proc::text, '') = ''
and	coalesce(nr_seq_conta_mat::text, '') = '';

select	count(1)
into STRICT	qt_item_proc_w
from	pls_conta_proc
where	nr_seq_conta = nr_seq_conta_p
and	ie_status	<> 'D';

select	count(1)
into STRICT	qt_item_lib_proc_w
from	pls_conta_proc
where	nr_seq_conta 	= nr_seq_conta_p
and	ie_status	in ('S','L','M');

select	count(1)
into STRICT	qt_item_mat_w
from	pls_conta_mat
where	nr_seq_conta = nr_seq_conta_p
and 	ie_status	<> 'D';

select	count(1)
into STRICT	qt_item_lib_mat_w
from	pls_conta_mat
where	nr_seq_conta 	= nr_seq_conta_p
and	ie_status	in ('S','L','M');

qt_item_w 	:= qt_item_mat_w + qt_item_proc_w;
qt_item_lib_w 	:= qt_item_lib_mat_w + qt_item_lib_proc_w;

if (qt_item_w = qt_item_lib_w) and (qt_item_w > 0) then
	update	pls_conta
	set	ie_status = 'L'
	where	nr_sequencia = nr_seq_conta_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
