CREATE OR REPLACE PROCEDURE pls_inativar_historico_compart ( nr_seq_segurado_p pls_segurado.nr_sequencia%type, dt_compartilhamento_p timestamp, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text) AS $body$
BEGIN

update	pls_segurado_historico
set	dt_atualizacao = clock_timestamp(),
	nm_usuario = nm_usuario_p,
	ie_situacao_compartilhamento = 'I'
where	ie_tipo_historico = '102'
and	nr_seq_segurado = nr_seq_segurado_p
and	dt_ocorrencia_sib > dt_compartilhamento_p
and	coalesce(ie_situacao_compartilhamento, 'A') = 'A';

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
