CREATE OR REPLACE PROCEDURE pls_inativar_planos_contrato (nr_seq_contrato_p bigint, dt_inativacao_p timestamp, ie_tipo_contratacao_p pls_plano.ie_tipo_contratacao%type, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		pls_contrato_plano.nr_sequencia%type;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	pls_contrato_plano a,
	pls_plano b
where	a.nr_seq_plano = b.nr_sequencia
and	a.nr_seq_contrato = nr_seq_contrato_p
and	b.ie_tipo_contratacao <> ie_tipo_contratacao_p
and	a.ie_situacao = 'A';


BEGIN

open c01;
loop
fetch c01 into	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL pls_inativar_contrato_plano(	nr_sequencia_w,
				dt_inativacao_p,
				nr_seq_contrato_p,
				cd_estabelecimento_p,
				nm_usuario_p);
	end;
end loop;
close c01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
