CREATE OR REPLACE PROCEDURE pls_incluir_cco_segurado ( nr_lote_p bigint) AS $body$
DECLARE


nr_seq_segurado_w		bigint;
nr_cco_w			bigint;
ie_digito_cco_w			smallint;
cd_cco_w			pls_segurado.cd_cco%type;

c01 CURSOR FOR
	SELECT	a.nr_sequencia,
		c.nr_cco,
		c.ie_digito_cco
	from	pls_segurado		a,
		pls_segurado_carteira	b,
		sib_devolucao_cco	c,
		pls_lote_sib		d
	where	b.nr_seq_segurado = a.nr_sequencia
	and	b.cd_usuario_plano = c.cd_usuario_plano
	and	c.nr_seq_lote = d.nr_sequencia
	and	d.nr_sequencia = nr_lote_p
	and	coalesce(a.nr_cco::text, '') = '';


BEGIN

open C01;
loop
fetch c01 into
	nr_seq_segurado_w,
	nr_cco_w,
	ie_digito_cco_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	cd_cco_w	:= lpad(nr_cco_w,10,0) || lpad(ie_digito_cco_w,2,0);
	update	pls_segurado
	set	nr_cco		= nr_cco_w,
		ie_digito_cco	= ie_digito_cco_w,
		cd_cco		= cd_cco_w
	where  	nr_sequencia	= nr_seq_segurado_w;

	end;
end loop;
close c01;

update	pls_lote_sib
set	ie_cco_atualizado	= 'S'
where	nr_sequencia		= nr_lote_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
