CREATE OR REPLACE PROCEDURE pls_incluir_documentacao_reaj ( nr_seq_prog_reaj_p bigint, nr_seq_relatorio_p bigint, ds_arquivo_p text, ds_documentacao_p text, nm_usuario_p text) AS $body$
DECLARE

 
qt_registros_w 	integer;
			

BEGIN 
 
select	count(1) 
into STRICT	qt_registros_w 
from	pls_prog_reaj_documentacao 
where	nr_seq_prog_reaj = nr_seq_prog_reaj_p;
 
if (qt_registros_w = 0) then -- O número máximo de registros é 1 
 
	insert into pls_prog_reaj_documentacao( 
			nr_sequencia, nr_seq_lote, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec, 
			nr_seq_prog_reaj, nr_seq_relatorio, ds_arquivo, ds_documentacao) 
		values (	nextval('pls_prog_reaj_documentacao_seq'), null, clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p, 
			nr_seq_prog_reaj_p, nr_seq_relatorio_p, ds_arquivo_p, ds_documentacao_p);
			 
end if;
 
commit;
 
end;
