CREATE OR REPLACE PROCEDURE pls_incluir_pf_via_adic ( nr_seq_segurado_p bigint, ie_processo_p text, ie_origem_solic_p text, ds_observacao_p text, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_carteira_w		bigint;
qt_beneficiarios_w		bigint;
nr_seq_lote_w			bigint;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_lote_w
from	pls_via_adic_cart_lote
where	coalesce(dt_aplicacao::text, '') = ''
and	coalesce(nr_seq_contrato::text, '') = ''
and	coalesce(cd_matricula_familia::text, '') = ''
and	coalesce(dt_adesao_inicial::text, '') = ''
and	coalesce(dt_adesao_final::text, '') = ''
and	ie_processo	= ie_processo_p;

if (coalesce(nr_seq_lote_w::text, '') = '') then
	select	nextval('pls_via_adic_cart_lote_seq')
	into STRICT	nr_seq_lote_w
	;

	insert	into pls_via_adic_cart_lote(	nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
			dt_solicitacao, ds_observacao, dt_geracao,ie_processo)
	values (	nr_seq_lote_w, clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
			clock_timestamp(), ds_observacao_p ,clock_timestamp(),ie_processo_p);
end if;

select	max(b.nr_sequencia)
into STRICT	nr_seq_carteira_w
from	pls_segurado_carteira b,
	pls_segurado a
where	a.nr_sequencia	= b.nr_seq_segurado
and	a.nr_sequencia	= nr_seq_segurado_p;

if (nr_seq_carteira_w IS NOT NULL AND nr_seq_carteira_w::text <> '') then
	select	count(*)
	into STRICT	qt_beneficiarios_w
	from	pls_via_adic_cart
	where	nr_seq_lote	= nr_seq_lote_w
	and	nr_seq_segurado	= nr_seq_segurado_p;

	if (qt_beneficiarios_w = 0) then
		insert	into	pls_via_adic_cart(	nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
				nr_seq_lote, nr_seq_carteira, nr_seq_segurado, ie_origem_solic)
		values (	nextval('pls_via_adic_cart_seq'), clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p,
				nr_seq_lote_w, nr_seq_carteira_w, nr_seq_segurado_p,ie_origem_solic_p);
	end if;
end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
