CREATE OR REPLACE PROCEDURE pls_incluir_tp_solic_alt_cad ( nr_seq_solicitacao_p bigint, nr_seq_alt_tabela_p bigint, nr_seq_contrato_p bigint, nr_seq_plano_p bigint) AS $body$
DECLARE


nr_seq_plano_w		bigint;
nr_seq_contrato_w	bigint;
nr_seq_contrato_inter_w bigint;

BEGIN

select	nr_seq_plano,
	nr_contrato,
	nr_seq_contrato_inter
into STRICT	nr_seq_plano_w,
	nr_seq_contrato_w,
	nr_seq_contrato_inter_w
from	pls_tabela_preco
where	nr_sequencia = nr_seq_alt_tabela_p;


if (nr_seq_plano_w = nr_seq_plano_p) and
(((nr_seq_contrato_w IS NOT NULL AND nr_seq_contrato_w::text <> '') and nr_seq_contrato_w = nr_seq_contrato_p) or ((nr_seq_contrato_inter_w IS NOT NULL AND nr_seq_contrato_inter_w::text <> '') and nr_seq_contrato_inter_w = nr_seq_contrato_p))then

	if (coalesce(nr_seq_solicitacao_p,0) <> 0) then
		update 	pls_segurado_solic_alt
		set	nr_seq_alt_tabela	= nr_seq_alt_tabela_p
		where 	nr_sequencia		= nr_seq_solicitacao_p;
	end if;

else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(358472);
end if;
commit;

end;
$body$
