CREATE OR REPLACE PROCEDURE pls_informar_motivo_incl_benef ( nr_seq_lote_inclusao_p bigint, nr_seq_inclusao_benef_p bigint, ie_tipo_operacao_p text, nr_seq_motivo_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_lote_inclusao_p IS NOT NULL AND nr_seq_lote_inclusao_p::text <> '') then
	if (ie_tipo_operacao_p = 'E') then
		update	pls_inclusao_beneficiario
		set	nr_seq_motivo_cancelamento	= nr_seq_motivo_p
		where	nr_seq_lote_inclusao		= nr_seq_lote_inclusao_p
		and	ie_tipo_operacao		= ie_tipo_operacao_p;
	elsif (ie_tipo_operacao_p = 'C') then
		update	pls_inclusao_beneficiario
		set	nr_seq_motivo_via	= nr_seq_motivo_p
		where	nr_seq_lote_inclusao	= nr_seq_lote_inclusao_p
		and	ie_tipo_operacao	= ie_tipo_operacao_p;
	end if;
elsif (nr_seq_inclusao_benef_p IS NOT NULL AND nr_seq_inclusao_benef_p::text <> '') then
	if (ie_tipo_operacao_p = 'E') then
		update	pls_inclusao_beneficiario
		set	nr_seq_motivo_cancelamento	= nr_seq_motivo_p
		where	nr_sequencia			= nr_seq_inclusao_benef_p;
	elsif (ie_tipo_operacao_p = 'C') then
		update	pls_inclusao_beneficiario
		set	nr_seq_motivo_via	= nr_seq_motivo_p
		where	nr_sequencia		= nr_seq_inclusao_benef_p;
	end if;
end if;

commit;

end;
$body$
