CREATE OR REPLACE PROCEDURE pls_informar_parecer_discussao ( nr_seq_disc_proc_mat_p bigint, ie_tipo_item_p text, ie_tipo_parecer_p text, ds_parecer_p text, nm_usuario_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Informar um parcer para os itens da discussão do A550
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

if (ie_tipo_item_p = 'P') then
	update	pls_discussao_proc
	set	ds_parecer_origem	= CASE WHEN ie_tipo_parecer_p='E' THEN ds_parecer_p  ELSE ds_parecer_origem END ,
		ds_parecer		= CASE WHEN ie_tipo_parecer_p='I' THEN ds_parecer_p  ELSE ds_parecer END ,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_disc_proc_mat_p;
elsif (ie_tipo_item_p = 'M') then
	update	pls_discussao_mat
	set	ds_parecer_origem	= CASE WHEN ie_tipo_parecer_p='E' THEN ds_parecer_p  ELSE ds_parecer_origem END ,
		ds_parecer		= CASE WHEN ie_tipo_parecer_p='I' THEN ds_parecer_p  ELSE ds_parecer END ,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_disc_proc_mat_p;
end if;

commit;

end;
