SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_iniciar_analise_grupo ( nr_seq_analise_p bigint, nr_seq_grupo_p bigint, nm_usuario_p text, ds_retorno_p INOUT text, ie_tipo_retorno_p INOUT text) AS $body$
DECLARE

 
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: Tratar início da análise do grupo auditor na nova Análise de Contas Médicas 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [X ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
 
nm_grupo_anterior_w		varchar(255);
nm_grupo_pedido_w		varchar(255);
nr_seq_ordem_w			bigint;
qt_pedido_parc_w		bigint;
nr_seq_grupo_atual_w		bigint;
dt_inicio_analise_w		timestamp;
dt_fim_analise_w		timestamp;


BEGIN 
/* Consistências */
 
 
/* Obter dados do grupo na análise */
 
select	a.nr_seq_ordem, 
	a.dt_inicio_grupo, 
	a.dt_fim_grupo 
into STRICT	nr_seq_ordem_w, 
	dt_inicio_analise_w, 
	dt_fim_analise_w 
from	pls_auditoria_conta_grupo a 
where	a.nr_seq_analise	= nr_seq_analise_p 
and	a.nr_seq_grupo		= nr_seq_grupo_p 
and	a.nr_sequencia		=	(SELECT	min(x.nr_sequencia) 
					from	pls_auditoria_conta_grupo	x 
					where	x.nr_seq_analise	= nr_seq_analise_p 
					and	x.nr_seq_grupo		= nr_seq_grupo_p 
					and	x.nr_seq_ordem		= (	select	min(y.nr_seq_ordem) 
										from	pls_auditoria_conta_grupo	y 
										where	y.nr_seq_analise	= nr_seq_analise_p 
										and	y.nr_seq_grupo		= nr_seq_grupo_p));
 
 
/* Verificar se tem algum grupo que precisa fazer a análise primeiro */
 
select	max(b.nm_grupo_auditor) 
into STRICT	nm_grupo_anterior_w 
from	pls_grupo_auditor b, 
	pls_auditoria_conta_grupo a 
where	a.nr_seq_grupo		= b.nr_sequencia 
and	a.nr_seq_analise	= nr_seq_analise_p 
and	a.nr_seq_grupo		<> nr_seq_grupo_p 
and	coalesce(a.dt_liberacao::text, '') = '' 
and	a.nr_seq_ordem		< nr_seq_ordem_w;
 
if (nm_grupo_anterior_w IS NOT NULL AND nm_grupo_anterior_w::text <> '') then 
	ds_retorno_p		:= 'O seu grupo de análise só pode iniciar após o grupo "' || nm_grupo_anterior_w || '" ter concluído a análise';
	ie_tipo_retorno_p	:= 'E';
end if;
 
if (coalesce(ds_retorno_p::text, '') = '') then 
	CALL pls_atualizar_auditor_grupo(nr_seq_grupo_p,nr_seq_analise_p,nm_usuario_p);
	 
	select	count(1), 
		max(nr_seq_grupo_atual) 
	into STRICT	qt_pedido_parc_w, 
		nr_seq_grupo_atual_w 
	from	pls_analise_pedido_parecer 
	where	nr_seq_grupo_parecer	= nr_seq_grupo_p 
	and	nr_seq_analise		= nr_seq_analise_p 
	and	coalesce(dt_finalizacao::text, '') = ''  LIMIT 1;
	 
	if (qt_pedido_parc_w > 0) then 
		select	max(nm_grupo_auditor) 
		into STRICT	nm_grupo_pedido_w 
		from	pls_grupo_auditor 
		where	nr_sequencia	= nr_seq_grupo_atual_w;
		 
		ds_retorno_p		:= 'O seu parecer foi solicitado novamente pelo grupo ' || nm_grupo_pedido_w;
		ie_tipo_retorno_p	:= 'I';
	end if;
	 
	commit;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_iniciar_analise_grupo ( nr_seq_analise_p bigint, nr_seq_grupo_p bigint, nm_usuario_p text, ds_retorno_p INOUT text, ie_tipo_retorno_p INOUT text) FROM PUBLIC;

