SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_ini_proces_xml_lote_web ( nr_seq_lote_p bigint) AS $body$
DECLARE


nr_seq_lote_xml_w	bigint;
ds_erro_w		varchar(2000);


BEGIN

if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then


	begin

	select	max(nr_seq_lote)
	into STRICT	nr_seq_lote_xml_w
	from	pls_xml_lote
	where	nr_sequencia = nr_seq_lote_p;

	update	pls_xml_lote
	set	ie_status = 'IMP',
		dt_inicio_confirmacao = clock_timestamp()
	where	nr_sequencia = nr_seq_lote_p
	and	ie_status = 'VAL';

	if (nr_seq_lote_xml_w IS NOT NULL AND nr_seq_lote_xml_w::text <> '') then
		update	pls_lote_protocolo_conta
		set	dt_confirmacao = clock_timestamp()
		where	nr_sequencia = nr_seq_lote_xml_w;
	end if;

	exception
	when others then
		ds_erro_w	:= substr(sqlerrm(SQLSTATE),1,2000);
		CALL pls_rejeitar_lote_imp_web(nr_seq_lote_p,ds_erro_w,'TASY-WEB');
	end;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
