CREATE OR REPLACE PROCEDURE pls_insere_arq_pedido (ie_tipo_arquivo_p text, nm_tabela_p text, nm_arquivo_p text, nr_sequencia_p text, nm_usuario_p text) AS $body$
DECLARE


ds_comando_update_w	varchar(4000);
ds_sep_bv_w		varchar(50);

BEGIN
ds_sep_bv_w := obter_separador_bv;
	if (ie_tipo_arquivo_p = '01') then
	ds_comando_update_w := 	' update  '||nm_tabela_p||
				' set     ds_arquivo_pedido = :nm_arquivo_p '||
				' where   nr_seq_guia 	    = :nr_sequencia_p ';
	elsif (ie_tipo_arquivo_p = '02') then
	ds_comando_update_w := 	' update  '||nm_tabela_p||
				' set     ds_arquivo_pedido = :nm_arquivo_p '||
				' where   nr_seq_execucao   = :nr_sequencia_p ';
	elsif (ie_tipo_arquivo_p = '03') then
	ds_comando_update_w := 	' update  '||nm_tabela_p||
				' set     ds_arquivo_pedido = :nm_arquivo_p '||
				' where   nr_seq_origem     = :nr_sequencia_p ';
	elsif (ie_tipo_arquivo_p = '04') then
	ds_comando_update_w := 	' update  '||nm_tabela_p||
				' set     ds_arquivo_pedido = :nm_arquivo_p '||
				' where   nr_seq_requisicao = :nr_sequencia_p ';
	end if;
	CALL exec_sql_dinamico_bv('OPS',ds_comando_update_w,	'nm_arquivo_p='||nm_arquivo_p||ds_sep_bv_w||
							'nr_sequencia_p='||nr_sequencia_p||ds_sep_bv_w);
	commit;

end;
$body$
